	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").addClass("icon-font").text("");
		$(".sw-dropdown-arrow").addClass("icon-font").text("");
		$("#sw-home-icon").addClass("icon-font").text("");
