		$(".sw-mystart-dropdown.schoollist .selector").text("Select a School");

		// ********** HEADER ********** //
		//DOC check if school is a subsite
		if("everettsd" != "everettsd") {
			$("#gb-logo").addClass("school-logo");
			$(".gb.footer.one").hide();
			$(".gb.footer.one.school-logo").show();
		}
		//DOC create search dropdown fucntion
		$("#gb-search-dropdown").click(function() {
			if($("#gb-mystart-search").hasClass("active")) {
				$("#gb-mystart-search").removeClass("active");
			} else {
				$("#gb-mystart-search").addClass("active");
			}
		});

		// ********** CHANNEL BAR ********** //
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

        //Hide caption links if empty
        $('.cs-custom-channel-dropdown-text-link').each(function() {
            var $this = $(this);
            if($this.html().replace(/\s| /g, '').length == 0)
                $this.remove();
        });

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        }
        //DOC add .icon-font class to div.ui-widget.app.navigation li
        $("div.ui-widget.app.navigation li").addClass("icon-font");
        //DOC prepend spans to .view-calendar-link
        $(".view-calendar-link").prepend("");
        //DOC prepend spans to .app-level-social-rss a.ui-btn-toolbar.rss
        $(".app-level-social-rss a.ui-btn-toolbar.rss").html("RSS Feed");

		//MOD EVENTS
		$(".ui-hp .upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".ui-hp .upcomingevents .ui-articles");

		// ********** FOOTER ********** //			

        //Added by Adam 11.17.17 - #02983348
