$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=9v6pfeOIHHd7NfPxMn62iw%3d%3d&SessionID=9970ceec-8e5f-4d09-8b11-829c51d8d60f&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+2%3a50%3a00+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.4.221';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
