(function($JssorSlider$) {
	'use strict';

	var maxWidthOrHeight = 
		document.getElementById('edlio_slider_container_12687').clientHeight;

	var maxNumberOfBullets = Math.round(
		maxWidthOrHeight / 35
	);
	var numberOfSlides = 3;

	var options = {
		$FillMode: 2,             
		$AutoPlay: true,          
		$LazyLoading: 1,          
		$AutoPlayInterval: 20000,  
		$SlideDuration: 500,      
		$PauseOnHover: 0,         
		$DragOrientation: 0,      
		$HWA: false,              
		$SlideshowOptions: {
			$Class: $JssorSlideshowRunner$,
			$Transitions: [
				{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}, // twin fade 

			],
			$TransitionsOrder: 0  
		},

		$ArrowNavigatorOptions: {                       
			$Class: $JssorArrowNavigator$,              
			$ChanceToShow: 2,                           
			$AutoCenter: 2,                             
			$Steps: 1                                   
		},
		$ThumbnailNavigatorOptions: {                   
			$Class: $JssorThumbnailNavigator$,          
			$ChanceToShow: 2,                           
			$ActionMode: 1,                             
			$SpacingX: 8,                               
			$DisplayPieces: 8,                          
			$ParkingPosition: 360,                      
			$Orientation: 
					2

		},
		$BulletNavigatorOptions: {                      
			$Class: $JssorBulletNavigator$,             
			$ChanceToShow: 2,                           
			$AutoCenter: 
					2
				,                                
			$Steps: 1,                                  
			$Lanes: Math.ceil(numberOfSlides / maxNumberOfBullets),              
			$SpacingX: 10,                              
			$SpacingY: 10,                              
			$Orientation: 
					2

		},
		$CaptionSliderOptions: {
			$Class: $JssorCaptionSlider$,
			$CaptionTransitions: [{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}],
			$PlayInMode: 1,
			$PlayOutMode: 3
		}

	};
	var edlioJssorSlider = new $JssorSlider$('edlio_slider_container_12687', options);


	function resizeEdlioSlider () {
		var parentWidth = document.getElementById('edlio_slider_container_12687').parentElement.clientWidth;

		if (parentWidth) {
			edlioJssorSlider.$ScaleWidth(parentWidth);
		} else {
			window.setTimeout(resizeEdlioSlider, 30);
		}
	}


		resizeEdlioSlider();







		window.addEventListener("load", resizeEdlioSlider);
		window.addEventListener("resize", resizeEdlioSlider);
		window.addEventListener("orientationchange", resizeEdlioSlider);


})($JssorSlider$);

// Set the date we're counting down to
var countDownDate = new Date("Jun 1, 2020");

// Update the count down every 1 second
var x = setInterval(function() {

// Get todays date and time
var now = new Date().getTime();

// Find the distance between now and the count down date
var distance = countDownDate - now;

// Time calculations for days, hours, minutes and seconds
var days = Math.floor(distance / (1000 * 60 * 60 * 24));
//var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
//var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
//var seconds = Math.floor((distance % (1000 * 60)) / 1000);

// Output the result in an element with id="demo"
document.getElementById("demo").innerHTML = days + " days until graduation!";
// " + hours + "h "+ minutes + "m " + seconds + "s ";

// If the count down is over, write some text
if (distance < 0) {
clearInterval(x);
document.getElementById("demo").innerHTML = "Congratulations to the Class of 2019!";
}
}, 1000);

footer.script:
  var link = document.getElementById("back-to-top");
  var amountScrolled = 300;

function addClass(el, className) {

    if (el.classList) {
        el.classList.add(className);
    } else {
        el.className += ' ' + className;
    }

}

function removeClass(el, className) {
    if (el.classList)
      el.classList.remove(className);
    else
      el.className = el.className.replace(new RegExp('(^|\\b)' + className.split(' ').join('|') + '(\\b|$)', 'gi'), ' ');
}

window.addEventListener('scroll', function(e) {
     if ( window.scrollY > amountScrolled ) {
         addClass(link, 'show');
     } else {
         removeClass(link, 'show');
     }
 });

