(function (window, document, $, undefined) {
    $(function () {
        $('.googtrans-trigger a').on('click', function (e) {
            window.location.href = (window.location.pathname + window.location.search + $(this).attr('href'));
            window.location.reload(true);
            return false;
        });
    });
})(window, document, jQuery);

