link.script:
    $(document).ready(function () {
        $(".LiquidButton").click(function () {
            if ($(".LiquidButton").attr("class").includes("LiquidDown")) {
                $(".LiquidHeader").addClass("Open");
                $(".LiquidHeader").addClass("navbar-fixed-top");
                var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) + 51;
                $('#TopUCs').css('margin-top', mt + 'px')
                $(".LiquidButton").addClass("LiquidUp");
                $(".LiquidButton").removeClass("LiquidDown");
            } else {
                $(".LiquidHeader").removeClass("Open");
                $(".LiquidHeader").removeClass("navbar-fixed-top");
                
                $(".LiquidButton").removeClass("LiquidUp");
                $(".LiquidButton").addClass("LiquidDown");
                var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) - 51;
                $('#TopUCs').css('margin-top', mt + 'px')
            }
        });
        if ($(".LiquidHeader").hasClass("Open")) {
            $(".LiquidHeader").addClass("navbar-fixed-top");
            var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) + 51;
            $('#TopUCs').css('margin-top', mt + 'px')
            $(".LiquidButton").removeClass("LiquidDown");
            $(".LiquidButton").addClass("LiquidUp");
        }
    });
    function RGBize(_img, pixelSize, _c = 1) {
        var canvas = document.createElement("canvas");
        canvas.width = $(_img).innerWidth();
        canvas.height = $(_img).innerHeight();

        var imgObj = new Image();
        imgObj.src = $(_img)[0].src;
        canvas.getContext('2d').drawImage(imgObj, 0, 0, canvas.width, canvas.height);
        var imgData = canvas.getContext('2d').getImageData(0, 0, canvas.width, canvas.height);
        var pixelData = imgData.data;
        var w = imgData.width;
        var h = pixelData.length / (w * 4);
        for (var i = 0; i < w * 4; i += pixelSize * 4) {
            for (var j = 0; j < h; j += pixelSize) {
                var r = 0;
                var g = 0;
                var b = 0;
                var a = 0;
                for (var ii = 0; ii < pixelSize; ii++) {
                    for (var jj = 0; jj < pixelSize; jj++) {
                        try{
                            r += pixelData[(i + 0) + (j * w * 4) + (ii * 4) + (jj * w * 4)];
                            g += pixelData[(i + 1) + (j * w * 4) + (ii * 4) + (jj * w * 4)];
                            b += pixelData[(i + 2) + (j * w * 4) + (ii * 4) + (jj * w * 4)];
                            a += pixelData[(i + 3) + (j * w * 4) + (ii * 4) + (jj * w * 4)];
                        }
                        catch(ex){}
                    }
                }
                var n = pixelSize * pixelSize;
                r /= n;
                g /= n;
                b /= n;
                a /= n;
                r = Math.round(r / _c) * _c;
                g = Math.round(g / _c) * _c;
                b = Math.round(b / _c) * _c;
                a = Math.round(a / _c) * _c;
                for (var ii = 0; ii < pixelSize; ii++) {
                    for (var jj = 0; jj < pixelSize; jj++) {
                        try{
                        imgData.data[(i + 0) + (j * w * 4) + (ii * 4) + (jj * w * 4)] = r;
                        imgData.data[(i + 1) + (j * w * 4) + (ii * 4) + (jj * w * 4)] = g;
                        imgData.data[(i + 2) + (j * w * 4) + (ii * 4) + (jj * w * 4)] = b;
                        imgData.data[(i + 3) + (j * w * 4) + (ii * 4) + (jj * w * 4)] = a;
                        }
                        catch(ex){}
                    }
                }
            }
        }
        canvas.getContext('2d').putImageData(imgData, 0, 0);
        $(_img).attr("src", canvas.toDataURL());
    }
    function RGBAll(){
        $.each($("img"), function(index, value){
            RGBize(value, 4, 40.3); 
        });
    }
    $(document).konami({
        result: RGBAll
    });
    $("header").sticky();

