$(document).ready(function() {
	$('.slide-pop').magnificPopup({
	  disableOn: 700,
	  type: 'inline',
	  midClick: true,
	  mainClass: 'mfp-fade',
	  removalDelay: 160,
	  preloader: false,
	  fixedContentPos: false
	});
	
	/*main-slider*/
	$('.main-slider').slick({
	  dots: true,
	  fade: true,
	  infinite: true,
	  adaptiveHeight: true, 
	  autoplay: true,
	  autoplaySpeed: '4000',
	  arrows: false,
	  appendDots: '.slickDots',
	});
});

			$(document).ready(function() {
				$('#facebookFeed .cmsFacebookFeed').slick({
				  dots: false,
				  infinite: true,
				  adaptiveHeight: true,
				  autoplay: true,
				  autoplaySpeed: 10000,
				  arrows: true,
				  appendArrows: '.fbArrows',
				  prevArrow: 'Previous',
				  nextArrow: 'Next'
				});
			});
			var feed = new Instafeed({
				get: 'user',
				userId: '7959495295',
				accessToken: '7959495295.1677ed0.30080e6f16054cf9a32993b52f56566b',
				target: 'instaFeed',
				resolution: 'standard_resolution',
				limit: 4,
				sortBy: 'most-recent',
				template: '

 / ashwaubenonsd{{model.template}}

{{model.created_time}}
{{caption}}Full Post',
				filter: function(image) {
					var date = new Date(image.created_time*1000);

					m = date.getMonth();
					d = date.getDate();
					y = date.getFullYear();

					var month_names = new Array ( );
					month_names[month_names.length] = "Jan";
					month_names[month_names.length] = "Feb";
					month_names[month_names.length] = "Mar";
					month_names[month_names.length] = "Apr";
					month_names[month_names.length] = "May";
					month_names[month_names.length] = "Jun";
					month_names[month_names.length] = "Jul";
					month_names[month_names.length] = "Aug";
					month_names[month_names.length] = "Sep";
					month_names[month_names.length] = "Oct";
					month_names[month_names.length] = "Nov";
					month_names[month_names.length] = "Dec";

					var thetime = month_names[m] + ' ' + d + ', ' + y;

					image.created_time = thetime;
					
					if (image.type == 'image' || image.type == 'carousel') {
						image.template = '
';
					} else {
						image.template = '
';
					}
					return true;
				},
				after: function() {
					$('#instaFeed').slick({
					  dots: false,
					  infinite: true,
					  adaptiveHeight: true,
					  autoplay: true,
					  autoplaySpeed: 10000,
					  arrows: true,
					  appendArrows: '.instaArrows',
					  prevArrow: 'Previous',
					  nextArrow: 'Next'
					});
				}	
			});
			feed.run();
