	function switchDip_310601(num,pause){
		clearTimeout(slideshowTimer_310601);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_310601();
		var speed = slideshowMotionSpeed_310601();
		var currentNum = document.getElementById('currentNum_310601').value;
		var totalDips = document.getElementById('totalDips_310601').value;
		var video = document.getElementById('video_310601').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_310601(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_310601' + nums).length){
				$('#nav_310601' + nums).removeClass('on');
			}
			
			if($('#nav_li_310601' + nums).length){
				$('#nav_li_310601' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_310601' + nums).length){
					$('#videoShell_310601' + nums).hide();
				}
				$('#dipImage_310601' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_310601' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_310601' + nums).length){
				$('#videoShell_310601' + nums).fadeOut(speed);
			}
			if($('#dipContent_310601' + nums).length){
				$('#dipContent_310601' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_310601' + nums).length){
				$('#nav_310601' + nums).addClass('on');
			}
			
			if($('#nav_li_310601' + nums).length){
				$('#nav_li_310601' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_310601' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_310601' + nums).length){
					$('#videoShell_310601' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_310601' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_310601' + nums).length){
					$('#videoShell_310601' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_310601' + nums).length){
				$('#dipContent_310601' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_310601(timer);
			document.getElementById('currentNum_310601').value = nums;
		}
	}
	
	function slideshowMotion_310601(){
