	function switchDip_402222(num,pause){
		clearTimeout(slideshowTimer_402222);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_402222();
		var speed = slideshowMotionSpeed_402222();
		var currentNum = document.getElementById('currentNum_402222').value;
		var totalDips = document.getElementById('totalDips_402222').value;
		var video = document.getElementById('video_402222').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_402222(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_402222' + nums).length){
				$('#nav_402222' + nums).removeClass('on');
			}
			
			if($('#nav_li_402222' + nums).length){
				$('#nav_li_402222' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_402222' + nums).length){
					$('#videoShell_402222' + nums).hide();
				}
				$('#dipImage_402222' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_402222' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_402222' + nums).length){
				$('#videoShell_402222' + nums).fadeOut(speed);
			}
			if($('#dipContent_402222' + nums).length){
				$('#dipContent_402222' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_402222' + nums).length){
				$('#nav_402222' + nums).addClass('on');
			}
			
			if($('#nav_li_402222' + nums).length){
				$('#nav_li_402222' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_402222' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_402222' + nums).length){
					$('#videoShell_402222' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_402222' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_402222' + nums).length){
					$('#videoShell_402222' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_402222' + nums).length){
				$('#dipContent_402222' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_402222(timer);
			document.getElementById('currentNum_402222').value = nums;
		}
	}
	
	function slideshowMotion_402222(){
		if($('#slideshowMotion_402222').length){
			slideshowMotion = document.getElementById('slideshowMotion_402222').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_402222(){
		if($('#slideshowMotionSpeed_402222').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_402222').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_402222(){
		if($('#slideshowQuickNavSpeed_402222').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_402222').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_402222(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

