div.script:
    $(document).ready(function() {
		
        	var t = setTimeout(function(){
				var alertHeight =  $('#announEmergencyShell').height();
                $('#announEmergencyShell').slideDown(700,function(){
					$('#announEmergencyShell').css('position', 'fixed');
					$('#alertSpacer ').css('height', alertHeight + 'px').css('display','block');
				});
            },1000);
		
        });

function event_view_CMS140691(eventid) {
	windowprops = "top=0,left=0,width=600,height=500,resizable=yes,scrollbars=yes,menubar=yes";
	url = "/pro/event_view.cfm?memberid=578&eventid=" + eventid;
	window.open(url, "event"+eventid, windowprops);
}
