			if($('#dipContent_412721' + nums).length){
				$('#dipContent_412721' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_412721' + nums).length){
