	function switchDip_550928(num,pause){
		clearTimeout(slideshowTimer_550928);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_550928();
		var speed = slideshowMotionSpeed_550928();
		var currentNum = document.getElementById('currentNum_550928').value;
		var totalDips = document.getElementById('totalDips_550928').value;
		var video = document.getElementById('video_550928').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_550928(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_550928' + nums).length){
				$('#nav_550928' + nums).removeClass('on');
			}
			
			if($('#nav_li_550928' + nums).length){
				$('#nav_li_550928' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_550928' + nums).length){
					$('#videoShell_550928' + nums).hide();
				}
				$('#dipImage_550928' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_550928' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_550928' + nums).length){
				$('#videoShell_550928' + nums).fadeOut(speed);
			}
			if($('#dipContent_550928' + nums).length){
				$('#dipContent_550928' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_550928' + nums).length){
				$('#nav_550928' + nums).addClass('on');
			}
			
			if($('#nav_li_550928' + nums).length){
				$('#nav_li_550928' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_550928' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_550928' + nums).length){
					$('#videoShell_550928' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_550928' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_550928' + nums).length){
					$('#videoShell_550928' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_550928' + nums).length){
				$('#dipContent_550928' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_550928(timer);
			document.getElementById('currentNum_550928').value = nums;
		}
	}
	
