(function(w, d, s, l, i) {
    w[l] = w[l] || [];
    w[l].push({
        'gtm.start': new Date().getTime(),
        event: 'gtm.js'
    });
    var f = d.getElementsByTagName(s)[0],
        j = d.createElement(s),
        dl = l != 'dataLayer' ? '&l=' + l : '';
    j.async = true;
    j.src =
        '//www.googletagmanager.com/gtm.js?id=' + i + dl;
    f.parentNode.insertBefore(j, f);
})(window, document, 'script', 'dataLayer', 'GTM-PWK9ZSL');

div.script:
window.fbAsyncInit = function() {
  //Initialize the Facebook JavaScript SDK
  FB.init({
    appId : '212315948831253', //App ID from the app dashboard
    channelUrl : 'http://www.uab.edu/news/channel.html', //Channel file for x-domain communication
    status : false, //Do not check Facebook Login status
    xfbml : true //Look for social plugins on the page
  });
  //Logged In Users
  FB.getLoginStatus(function(response) {
    if (response.status !== "unknown") { 
      ga('set', 'dimension1', 'Logged In');
    }
  });
  //Facebook Likes 
  FB.Event.subscribe('edge.create', function(href, widget) {
    var currentPage = jQuery(document).attr('title');
    ga('send', {
      'hitType': 'social',
      'socialNetwork': 'Facebook',
      'socialAction': 'Like',
      'socialTarget': href,
      'page': currentPage
    });
  });
  //Facebook Unlikes
  FB.Event.subscribe('edge.remove', function(href, widget) {
    var currentPage = jQuery(document).attr('title');
    ga('send', {
      'hitType': 'social',
      'socialNetwork': 'Facebook',
      'socialAction': 'Unlike',
      'socialTarget': href,
      'page': currentPage
    });
  });
  //Facebook Send/Share
  FB.Event.subscribe('message.send', function(href, widget) {
    var currentPage = jQuery(document).attr('title');
    ga('send', {
      'hitType': 'social',
      'socialNetwork': 'Facebook',
      'socialAction': 'Send',
      'socialTarget': href,
      'page': currentPage
    });
  });
  //Facebook Comments
  FB.Event.subscribe('comment.create', function(href, widget) {
    var currentPage = jQuery(document).attr('title');
    ga('send', {
      'hitType': 'social',
      'socialNetwork': 'Facebook',
      'socialAction': 'Comment',
      'socialTarget': href,
      'page': currentPage
    });
  });
};
//Load the SDK asynchronously
(function(d, s, id) {
var js, fjs = d.getElementsByTagName(s)[0];
if (d.getElementById(id)) return;
js = d.createElement(s); js.id = id;
js.src = "//connect.facebook.net/en_GB/all.js";
fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));

