div.script:
$(document).ready(function () {
    $("#8fddcddc-a516-4d74-91da-5a52dcf8a5c7").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 6000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 6 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#521aa3d2-c2c7-46d7-ac73-22468c57d325").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#521aa3d2-c2c7-46d7-ac73-22468c57d325').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#521aa3d2-c2c7-46d7-ac73-22468c57d325').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#d1430350-8cc8-42ad-92f0-9a6b35755931').hide();
    jQuery('#d1430350-8cc8-42ad-92f0-9a6b35755931').listSolrResults({
        currentPagePath: '/content/schools/azva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'd1430350-8cc8-42ad-92f0-9a6b35755931',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

