link.script:
	$(window).load(function(){
		searchText();
        stickyHeader();
        $('#slideshow-sidebar').css('height', $('#hp-slideshow').outerHeight());
	});

	$(function(){
		$(window).bind("resize", function(){ pgScreenSize(); calendarListView(); stickyHeader(); });
        $(window).scroll(function() { stickyHeader(); });

        var extraMenuItems = "";
        var extraMenuOptions = "";
        var menuOptionPos = 1;
        var hitsFound = 0;

        var acoeDivisionLinks = {
        	"Superintendent": ["/Page/128", "_self"],
            "Board": ["/board", "_self"],
            "Learning and Accountability": ["/Page/51", "_self"],
            "Student Programs and Services": ["/Page/57", "_self"],
            "Business Services": ["/Page/53", "_self"],
            "Human Resources": ["http://alameda.schoolwires.net/Page/55", "_self"],
            "": ["", "_blank"]
        };

        $.each(acoeDivisionLinks, function(k, v){
			if( k != "" ) {
            	extraMenuItems += menuOptionPos + ", " + k + ", " + acoeDivisionLinks[k][0] + ", " + acoeDivisionLinks[k][1] + ";";
            	hitsFound = 1;
            }
        });

        if( hitsFound ){
        	hitsFound = 0;
            menuOptionPos++;
        	extraMenuOptions = "ACOE DIVISIONS"
        }





		// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left   
			"primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			"secondaryColor"        : "#222A30", // DEFAULT - #222A30
			"accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#sticky-gb-channel-list-outer",
			"showMenuBtnText"       : "yes",
			"menuBtnText"           : 'MAIN MENU ',
			"showSchools"           : "yes",
			"showTranslate"         : "yes",
			"translateId"           : "none",
			"showAccount"           : "yes",
			"extraMenuOptions"      : extraMenuOptions, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"        : extraMenuItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"                : "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			"primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			"secondaryColor"        : "#222A30", // DEFAULT - #222A30
			"accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#sp-content",
			"showMenuBtnText"       : "yes",
			"menuBtnText"           : 'PAGES ',
			"siteID"                : "4"
		});

        dynamicContent();
        modEvents();
		pgScreenSize();
		useChannelForSection();
		customDropdowns('point-up', 'point-down');
        addFramesetTranslate();
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { //Desktop View
			multimediaRotator();
		}else{ //Mobile BPs
			if(getWidth > 479){
				multimediaRotator();
			}
			appView();
		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                "imageWidth"              : 790,
                "imageHeight"             : 494,
                "efficientLoad"           : "yes",
                "appHeader"               : "no",
                "titles"                  : "yes",
                "captions"                : "yes",
                "galleryOverlay"          : "no",
                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                "playPause"               : "no",
                "mainControls"            : "yes",
                "contMoreLink"            : "no",
                "contMoreLinkText"        : "Read More",
                "descMoreLink"            : "no",
                "descMoreLinkText"        : "Read More",
                "bullets"                 : "no",
                "bulletControls"          : "no",
                "thumbnails"              : "no",
                "thumbnailWidth"          : 122,
                "thumbnailHeight"         : 53,
                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                "hoverPause"              : "yes",
                "autoRotate"              : "yes",
                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                "fullScreenVideo"         : "no",
                "transitionType"          : "fade", // fade or slide
                "transitionSpeed"         : 3,
                "transitionDelay"         : 8,
                "onImageLoad"             : function() { },
                "allImagesLoaded"         : function() { },
                "onThumbnailLoad"         : function() { },
                "allThumbnailsLoaded"     : function() { },
                "onTransitionStart"       : function() { },
                "onTransitionEnd"         : function() { },
                "watchVidClick"           : function() { },
                "closeVidClick"           : function() { },
                "allLoaded"               : function() { 
                	if( $('#hp-slideshow .rs-gallery-control.back').length > 0 ){
                    	$('#hp-slideshow .rs-gallery-control.back').append('prev');
                        $('#hp-slideshow .rs-gallery-control.next').append('next');
                    }

                    galleryTabbing();
                }
			});
		}
	}

    function galleryTabbing(){
    	$('.gallery-display-control').click(function(e){
            $('.gallery-display-control.active').removeClass('active');
            $(this).addClass('active');

			if( $(this).attr('id') == "gallery-show-caption" ){
            	$('#hp-slideshow .rs-gallery-desc').addClass('show-it');

                if( !$('#gallery-button-feature').hasClass('no-longer-needed')) $('#gallery-button-feature').fadeOut(400).addClass('no-longer-needed');
            }else{
            	$('#hp-slideshow .rs-gallery-desc').removeClass('show-it');
            }
        });
    }

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "yes",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });

        $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing


			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});
		});
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$(".accordion-area div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				if( !$(".ui-widget-header", this).hasClass('custom-header') ) $(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header > h1 .accordion-handle', this).length <= 0){
					$('.ui-widget-header > h1:not(.extra-line)', this).append('');
				}
			}
		});

		//DOC accordion header click
		$(".accordion-area div.ui-widget-header").off('click').click(function(){
            if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
            	$(".accordion-area div.ui-widget.app.active .accordion-handle").removeClass('point-down');
                $(this).parent().removeClass("active");
            }else{
                var clickedApp = $(this).parent().parent().attr("id");
                $(".accordion-area div.ui-widget.app.active .accordion-handle").removeClass('point-down');
                $(".accordion-area div.ui-widget.app.active").removeClass("active"); 
                $(this).parent().addClass("active");
                $(".accordion-area div.ui-widget.app.active .accordion-handle").addClass('point-down');
                smoothScroll("#" + clickedApp, 10);
            }
		});


        if( $('#hp-row-two .right-side .region > div:first .ui-widget-header h1').length <= 0 && $('#hp-row-one .left-side .region > div:first .ui-widget-header h1').length > 0 ){ //if the first app header on the right side doesn't exist then the app header in the first column will handle showing the whole row, otherwise do nothing different.
        	$('#hp-row-two').addClass('hidden-row');

            $('#hp-row-two .right-side .ui-widget-header').off('click');
            $('#hp-row-two .left-side .ui-widget-header').click(function(e){
            	if( $('#hp-row-two').hasClass('hidden-row') ){
                	$('#hp-row-two').removeClass('hidden-row').addClass('showing-row');
                }else{
                	$('#hp-row-two').removeClass('showing-row').addClass('hidden-row');
                }
            });
        }
	}	

