link.script:
function gt(value) {
	if(!value) {
		alert("Empty");
		return;
	}
var windowResult = false;
try{
	windowResult = window.open(value);
	if (!!!windowResult) window.location.href=value;
	} catch (e) {
		alert(e);
	}
}

div.script:
var script = document.createElement('script');
script.src = "https://cdnjs.cloudflare.com/ajax/libs/tota11y/0.1.6/tota11y.min.js";

if(getQueryString('tota11y') === 'true') {
	document.head.append(script);
}	

function getQueryString (field, url) {
	var href = url ? url : window.location.href;
	var reg = new RegExp( '[?&]' + field + '=([^&#]*)', 'i' );
	var string = reg.exec(href);
	return string ? string[1] : null;
}

window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-163690097-1');

body.script:
window.onload = init;
var topnavButton = document.getElementById('topnav_mobile');
var topnavDisplay = document.getElementById('topnav');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
