            });

        },

        "CreateCookie": function(name, value, days) {
            var expires;

            if (days) {
                var date = new Date();
                date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                expires = "; expires=" + date.toGMTString();
            } else {
                expires = "";
            }
            document.cookie = escape(name) + "=" + escape(value) + expires + "; path=/";
        },
        "EraseCookie": function(name) {
            this.CreateCookie(name, "", -1);
        },

        "ReadCookie": function(name) {
            var nameEQ = escape(name) + "=";
            var ca = document.cookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                if (c.indexOf(nameEQ) === 0) return unescape(c.substring(nameEQ.length, c.length));
            }
            return null;
        },

        "TextSizeContrastBarAddOn": function(){
        	var _this = this;
            var textSize = this.ReadCookie("csTextSize");
            this.TextSizeOn = (textSize) ? true : false;

            if( $('#cs-contrast-bar #text-size-toggles').length <= 0 ){
            	$('#cs-contrast-bar').append('
\

Text Size\

\
                        	Default\
                            Medium\
                            Large\
                        \
                     ');
           	}

            if(this.TextSizeOn){
            	$("html").addClass("cs-text-size " + textSize);
            	$('.cs-text-size-toggle').attr('data-status', 'off');
                $('.cs-text-size-toggle[data-text-size="' + textSize + '"]').attr('data-status', 'on');
			}

            $('.cs-text-size-toggle').off();
        	$('.cs-text-size-toggle').click(function(e) {
                if(_this.TextSizeOn && ($(this).attr('data-status') == "on" || $(this).attr('data-text-size') == "cs-text-normal")) {
                    $("html").removeClass("cs-text-size cs-text-medium cs-textlarge");
                    $('.cs-text-size-toggle').attr('data-status', 'off');
                    $('#cs-normal-text-size.cs-text-size-toggle').attr('data-status', 'on');

                    _this.EraseCookie("csTextSize");

                    _this.TextSizeOn = false;
                } else if( $(this).attr('data-text-size') != "normal" ){
                    $("html").removeClass('cs-text-medium cs-text-large').addClass("cs-text-size " + $(this).attr('data-text-size'));
                    $('.cs-text-size-toggle').attr('data-status', 'off');
					$(this).attr('data-status', 'on');

                    _this.CreateCookie("csTextSize", $(this).attr('data-text-size'));

                    _this.TextSizeOn = true;
                }
            });
        },

        "Header": function() {

            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/CA01902711/Centricity/Template/GlobalAssets/images///img/logo.png");
            if(showLogo) {
                if((logoSrc != "") && logoSrc.indexOf("default-man") == -1) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }
            }

            // ADD SITENAME
            var siteNameOne = $.trim("Rosemead School District");
            var siteNameTwo = $.trim("Established in 1859");
            var siteNameBegin = '';
            var siteNameEnd = '';

            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Rosemead School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //ADJUST WITH OF CHANNEL BAR DROPDOWN WIDTH IF THERE ARE ONLY 2 CHILDREN
            $(".sw-channel-dropdown").each(function() {
            	var count = $(this).children().length;

               	if(count < 3) {
                	$(this).addClass('halfsize');
                }

                if(count < 2) {
                	$(this).addClass('thirdsize');
                }

            });	


        },

        "Body": function() {
   			var _this = this;
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
