            $('.headlines .ui-widget-detail').prepend('');

            //FOR HOMEPAGE
            $('#hp-headlines .ui-widget-detail .ui-articles li').slice(0,4).prependTo('.featured-ui-articles');


            //FOR SUBPAGE
            $('.sp .headlines .ui-widget-detail .ui-articles li').slice(0,2).prependTo('.featured-ui-articles');
            $('.spn .headlines .ui-widget-detail .ui-articles li').slice(0,2).appendTo('.featured-ui-articles');
            $('.sp .ui-read-more').prependTo('.headlines .ui-widget-footer');

            //MOVE 'MORE ARTICLES' LINK
            $('#hp-headlines .ui-read-more').insertBefore('#hp-headlines button');

            //CONTENT EXPANDER INIT
            $('#hp-headlines .ui-widget-detail li:gt(1)').find('a').attr('tabindex', '-1');
            $('#hp-headlines .ui-widget-detail li:gt(1)').attr('aria-hidden', true);
            $('#hp-headlines .ui-read-more a, #hp-headlines a.rss').attr('tabindex', '-1');

            if($('#hp-headlines .headlines .ui-widget-header h1').length) {
                var header = $('#hp-headlines .headlines .ui-widget-header h1').text();
            }
            else {
            	var header = "Headlines & Features";
            }

            $('#hp-headlines button span').text(header);


    		//CONTENT EXPANDER FUNCTIONALITY

            $('#hp-headlines button').on('click keydown', function(e) {
					if(_this.AllyClick(e)) {
                        e.preventDefault();

                        var initialHeight = 713;
                        var scrollHeight = $("#hp-headlines").prop("scrollHeight");

                        if ($(this).hasClass("open") ) {
                          $(this).removeClass('open').attr('aria-expanded', false);
                          $('#hp-headlines').css('height', initialHeight + 'px').css('overflow', 'hidden');
                          $('#hp-headlines .ui-widget-detail li:gt(1)').find('a').attr('tabindex', '-1');
                          $('#hp-headlines .ui-widget-detail li:gt(1)').attr('aria-hidden', true);
                          $('#hp-headlines .ui-read-more a').attr('tabindex', '-1');
                          $('#hp-headlines a.rss').attr('tabindex', '-1');
                          $(this).html('More '+ header +'');

                        }else {
                            $(this).addClass('open').attr('aria-expanded', true);;
                            $('#hp-headlines').css('height', scrollHeight + 'px')
                            $('#hp-headlines .ui-widget-detail li').find('a').attr('tabindex', '0');
                            $('#hp-headlines .ui-widget-detail li').attr('aria-hidden', false);
                            $('#hp-headlines .ui-read-more a.rss').attr('tabindex', '-1');
                            $('#hp-headlines a.rss').attr('tabindex', '0');
                            setTimeout(function(){ 
                            	$('#hp-headlines .ui-widget-detail li:eq(2)').find('a').focus();
                                $('.ie #hp-headlines').css('overflow', 'visible');
                            },500);

                            $(this).html('Close '+ header +'');

                        }

                    }

            });




        },

