        "Quicklinks":function() {
        	var _this = this;
        	var linkBank = [
                {
                    "show": true,
                    "text": "Budget: LCFF/LCAP",
                    "url": "https://www.rosemead.k12.ca.us/domain/57"

                },
                {
                    "show": true,
                    "text": "Citizens&#39 Bond Oversight Committee",
                    "url": "https://www.rosemead.k12.ca.us/domain/61#"
                },
                {
                    "show": true,
                    "text": "Title IX Information & Coordinators",
                    "url": "https://www.rosemead.k12.ca.us/Domain/253"
                }
            ];

            var links = '';
            $.each(linkBank, function(index, link) {
                if(link.show) {
                    links += '
' + link.text  + '';
                }
            });

            if(links.length) {
                $("#quicklinks").html('' + links + '');
            }

            //HIDE QUICKLINKS SECTION IF EMPTY
            $("#links li a:empty").parent().remove();
            var listItems = $('#links').children().length;
            if(!$(listItems).length) {
            	$('#hp-quicklinks').remove();
            }


            //TOGGLE QUICK LINKS
            $('#ql-button').bind('click keydown', function(e) {
            		if(_this.AllyClick(e)) {
                  		e.preventDefault();

                        if($(this).attr('aria-expanded') == "false") {
            				$(this).attr('aria-expanded',true);
                            $('#links').slideDown();
                            $('#links li:eq(0)').find('a').focus();


            			}
                        else {
                        	$(this).attr('aria-expanded',false);
                            $('#links').slideUp();

                        }
                    }

                    // CONSUME LEFT AND UP ARROWS
                    if(e.keyCode === 37 || e.keyCode === 38) {
                        e.preventDefault();
                        $('#links li:last-child').find('a').focus();

                    }

                    // CONSUME RIGHT AND DOWN ARROWS
               	    if(e.keyCode === 39 || e.keyCode === 40) {
                    	e.preventDefault();
                    	$('#links li:first-child').find('a').focus();
                    }

            });

            //QUICKLINKS KEYBOARD FUNCTIONALITY

            $('#links a').keydown(function(e){

            	// CONSUME LEFT AND UP ARROWS
            	if(e.keyCode === 37 || e.keyCode === 38) {
                	e.preventDefault();
                    if($(this).parent().index() == 0) {
                    	$('#ql-button').focus();


                    }else {
                    	$(this).parent().prev("li").find("a").focus();
                    }

                }

                // CONSUME RIGHT AND DOWN ARROWS
                if(e.keyCode === 39 || e.keyCode === 40) {
                	e.preventDefault();
                    if($(this).parent().is(":last-child")) {
                    	$('#ql-button').focus();


                    }else {
                    	$(this).parent().next("li").find("a").focus()
                    }

                }

                // CONSUME ESC KEY
				if(e.keyCode === 27) {
                	e.preventDefault();
                    	$('#ql-button').focus().attr('aria-expanded',false);
                        $('#links').slideUp('fast');
           		};

                // CONSUME SHIFT+TAB COMBO
                if (e.shiftKey && e.keyCode === 9) {
                    if($(this).parent().is(":first-child")) {
                		$('#ql-button').focus();
                    }
                }

                $('#hp-quicklinks').on('focusout', function() {
                	setTimeout(function () {
