            GetContent("https://www.seeleyusd.org//site/UserControls/Calendar/CategoryFilterListWrapper.aspx?ModuleInstanceID=" + "93" + "&Expanded=" + true, "calendar-pnl-categoryfilter-open", 3, callback);
        } else {
             var callback = "$('#calendar-pnl-categoryfilter-open').fadeOut('fast',function() { $('#calendar-pnl-categoryfilter-closed').fadeIn('fast');});";
             GetContent("https://www.seeleyusd.org//site/UserControls/Calendar/CategoryFilterListWrapper.aspx?ModuleInstanceID=" + "93" + "&Expanded=" + false, "calendar-pnl-categoryfilter-closed", 3, callback); 
         }
     }

