                            "descMoreLinkText" : "Read More",
                            "bullets" : "no",
                            "bulletControls" : "no",
                            "thumbnails" : "no",
                            "thumbnailWidth" : 122,
                            "thumbnailHeight" : 53,
                            "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                            "hoverPause" : "yes",
                        "autoRotate" : "yes",
