function updateTotalWithFee(){

	var dstr = '';
	var astr = '';
	var fee = '';

	dstr = +$('#damount').val().replace(/[^\d.]+/g,'');
	
	if(dstr > 0){
		
		//var percent = 0.05;
		var percent = 0.04;
		
		fee = dstr * percent;
		fee = Number(Math.round(fee+'e2')+'e-2');		
		//fee = (fee + 0.3).toFixed(2);
		fee = fee.toFixed(2);
		
	}else{
		fee = (0).toFixed(2);
	}
		   
	if ($("#cover_fee").is(":checked")) {
    
    	if($("#fee_amount").data("pressed") && +$("#fee_amount").val() <= fee){
        	fee = parseFloat($("#fee_amount").val()).toFixed(2);
			$("#fee_amt").html(fee);
    	}
    
		if(+$("#fee_amount").val() > fee){
        	$("#fee_amount").val(fee);
			$("#fee_amt").html(fee);
    	}else{
        	$("#fee_amount").val(fee);
			$("#fee_amt").html(fee);
    	}
       
		var astr = (+dstr + +fee).toFixed(2);
            
		//$("#fee_box").show();
		//$('#fee_box').css('display', 'inline-block');
		$("#dtotal").text(astr);
		
	}else if($("#cover_fee").is(":not(:checked)") || $("#cover_fee").length < 1 ){

		$("#dtotal").text(dstr);
		$("#fee_amount").val(0);
		$("#fee_amt").html(fee);
		
	}
    $("#fee_amount").data('pressed', false);
}


//CHECK INPUTS FOR A MULTIPLE CHARITY DONATION
 		$('.amt').blur(function () {
            var sum = 0;
            var val = 0;

            $('.amt').each(function () {
                //var val = parseFloat(this.value);
                var val = this.value;
                val = val.replace(/[^\d.]+/g,'');
                val = parseFloat(val);
                
                if (val >= 5) {
                    sum += !isNaN(val) ? val : 0;
                } else if (val < 5) {
                    //alert("The minimum donation to each charity is $5.");
                    $('#amounterror').html('The minimum donation to each charity is $5.');
                    $('#amounterror').show();
                    $(this).focus();
                    return false;
                }
            });

            $('#damount').val(sum.toFixed(2));
            $('#dtotal').html(sum.toFixed(2));
            updateTotalWithFee();
        });
