function toggleTribute(){
	trib = $('input[name=tribute]:checked').val();
	//alert(trib);
	if (trib == 'In Honor Of') {
		$("#honor").addClass('on').removeClass('off');
		$("#memory").addClass('off').removeClass('on');
	}
	else if (trib == 'In Memory Of') {
		$("#memory").addClass('on').removeClass('off');
		$("#honor").addClass('off').removeClass('on');
	}
	else {
		$("#memory").addClass('off').removeClass('on');
		$("#honor").addClass('off').removeClass('on');
	}	
	
}


function toggleTributeAmt(){
	val = $('input[name=Tribute_IncludeAmount]:checked').val();
	alert(val);
	if (val = 1) {
		$("#ia").addClass('on').removeClass('off');
		$("#notia").addClass('off').removeClass('on');
	}
	else if (val = 0) {
		$("#ia").addClass('off').removeClass('on');
		$("#notia").addClass('on').removeClass('off');
	}
	else {
		$("#notia").addClass('off').removeClass('on');
		$("#ia").addClass('off').removeClass('on');
	}		
}




