		$('#password').change(function(){
			
			var pass = $('#password').val();
			
			if(pass.length < 6 || pass.length > 15){
				alert('The password must be at least 6 and no more than 15 characters.');
				$('#password').val('');
				$('#password').focus();
				return false;
			}
			
			var capletter = /[A-Z]/.test(pass)
		
			if( !capletter ){

				alert( 'The password must contain at least one upper case letter. Please re-enter the password.' );        
				$('#password').val('');   
				$('#password').focus();        
				return false;
			}

			var spechar = /[^a-zA-Z0-9]/.test(pass);

			if( !spechar ){

				alert( 'The password must contain at least one special character like # $ % _ ) ( * & < >. Please re-enter the password.' );        
				$('#password').val('');      
				$('#password').focus();        
				return false;
			}
			
			return true;
		});
		
