        function checkCard($card) {
            var c = $card;
			c = c.replace(/\D/g,'');
            var cl = parseInt(c.substr(c.length - 1));
            var c = c.slice(0, -1)
            var c = c.split("").reverse().join("");
            var c = c.split("");
            var a = 2;
            var cm = [];
            for (var i = 0; i < c.length; i++) {

                if (a % 2 == 0) {
                    var t = c[i] * 2;
                    if (t > 9) {
                        var t = (t - 9);
                    }
                    cm.push(t);
                } else {
                    cm.push(parseInt(c[i]));
                }
                a++;
            }

            var f = 0;

            for (var i = 0; i < cm.length; i++) {
                f += cm[i];
            }

            f = f + cl;

            if (f % 10 == 0) {
                return true;
            }
            else {
                return false;
            }
        }

        $('#ccard').change(function () {
            var ccard = $('#ccard').val();
            var result = checkCard(ccard);

            if (result === true) {

                $('#carderror').hide();

                return true;

            } else {

                $('#carderror').html("Please enter a valid credit card number");
                $('#carderror').show();
                $('#ccard').focus();
                return false;
            }
        });

        $('#year,#month').change(function (event) {

            var d = new Date();

            var month = d.getMonth() + 1;

            var day = d.getDate();

            var today = d.getFullYear() + '/' +(month < 10 ? '0' : '') + month + '/' +(day < 10 ? '0' : '') + day;

            var expyear = 20 + $('#year').val();
            var expmonth = $('#month').val();
            var expdate = expyear + '/' + expmonth + '/' + 28;

//alert('Exp:'+expdate+'   Today:'+today);

            if (new Date(expdate) <= new Date(today)) {

                $('#experror').html('Please enter a valid expiration date');

                $('#experror').show();

                $('#month').focus();

                return false;

            } else {

                $('#experror').hide();

                return true;

            }

        });



