    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ChannelBarIsSticky": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
			this.MyStart();
			this.SchoolList();
			this.Translate();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.Slideshow();
            this.StreamingVideo();
            this.Body();
            this.ModEvents();
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.FindItFastLinks();
            this.Footer();
            this.SocialIcons();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			csGlobalJs.OpenInNewWindowWarning();
            $("#sw-mystart-outer").remove();

            $(".hp .ui-widget.app.headlines").each(function() {
            	var thisHApp = this;
                if($("a.more-link",thisHApp).length && $(".ui-widget-header",thisHApp).length) {
                	$(thisHApp).find(".more-link").appendTo($(thisHApp).find(".ui-widget-header"));
                    $(thisHApp).find(".ui-widget-header").addClass("displayFlexRow");
                }
            });

            $(".ui-widget.app.headlines .cs-headline-info.has-thumb").each(function() {
                  var thisInfo = this;

                  $(thisInfo).parent().parent().addClass("has-thumb");
            });

            $(".headlines .cs-article-viewer .ui-articles").each(function() {
            	var getThumbHeight = $("li:eq(1)",this).find(".ui-article-thumb").height();
                $("li:eq(0)",this).find(".ui-article-thumb").css({
                	"max-height": getThumbHeight + "px"
                });
            });

            if($(".sp #sw-content-layout12 #sw-content-container2 .ui-widget.app.headlines").length || $(".spn #sw-content-layout12 #sw-content-container2 .ui-widget.app.headlines").length) {
            	this.spTabbedHeadlines();
            }
        },

        "WindowResize": function() {
            this.JsMediaQueries();

            $(".headlines .cs-article-viewer .ui-articles").each(function() {
            	var getThumbHeight = $("li:eq(1)",this).find(".ui-article-thumb").height();
                $("li:eq(0)",this).find(".ui-article-thumb").css({
                	"max-height": getThumbHeight + "px"
                });
            });

            if($(window).width() > 1023) {
                if($("#hp-alert .ui-widget.app.announcements .ui-article").length) {
                    var channelHeight = $("#gb-header-outer").height();
                    channelHeight = channelHeight + 1;
                    $("#hp-alert-outer").css({
                        "top": channelHeight + "px"
                    });
                }
            }
        },

        "WindowScroll": function() {
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;


            	/*var loginUrl = $(".sw-mystart-button.signin a").attr("href");
            	$(".gb-footer-link.login").attr("href", loginUrl);*/

