                        },
                        {
                            "show": true,
                            "text": "Parent Portal",
                            "url": "https://d49.powerschool.com/public/",
                            "target": "_blank"
                        },
                        {
                            "show": true,
                            "text": "Schoology",
                            "url": "https://www.schoology.com",
                            "target": "_blank"
                        },
                        {
                            "show": true,
                            "text": "PaySchools",
                            "url": "https://payschools.com",
                            "target": "_blank"
                        },
