                    } else {
                    	$(".cs-mystart-dropdown.signin").remove();
                    }

                    // BIND DROPDOWN EVENTS
                    this.DropdownActions({
                        "dropdownParent": ".cs-mystart-dropdown.signin",
                        "dropdownSelector": ".cs-selector",
                        "dropdown": ".cs-dropdown",
                        "dropdownList": ".cs-dropdown-list"
                    });

                if($(".sw-mystart-button.register").length) {
                	var registerUrl = $(".sw-mystart-button.register a").attr("href");;
                    $(".gb-footer-link.login").after('Register')
                }


            var districtHome = $(".sw-mystart-button.home a").attr("href");
            $(".gb-district-btn").attr("href",districtHome);
        },

		"SchoolList": function() {
        	var _this = this;

        	$.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 59127,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED CATEGORIES IN THE ORDER WE WANT
                    var expectedNameOrder = ["district", "falcon zone", "power zone", "sand creek zone", "iconnect zone"];
                    schoolList.sort(function(a, b) {
                        if(expectedNameOrder.indexOf(a.schoolCategory.toLowerCase()) < expectedNameOrder.indexOf(b.schoolCategory.toLowerCase())) {
                            return -1;
                        }
                        if(expectedNameOrder.indexOf(a.schoolCategory.toLowerCase()) > expectedNameOrder.indexOf(b.schoolCategory.toLowerCase())) {
                            return 1;
                        }
                        return 0;
                    });

                    // now SORT THE SCHOOLS INTO CATEGORY OBJECTS SO THEY'RE EASY TO WORK WITH
                    var sortedSchools = {};
                    for(var i in schoolList) {
                        if($.trim(schoolList[i].schoolVisible).toLowerCase() == 'true' || $.trim(schoolList[i].schoolVisible).toLowerCase() == "1") {
                            var thisCategory = $.trim(schoolList[i].schoolCategory).toLowerCase();

                            // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                            if(schoolList[i].schoolCategory.indexOf(",") > -1) {
                                var subCategories = schoolList[i].schoolCategory.split(",");

                                // LOOP THROUGH THE SUB TYPES
                                for(var subCat in subCategories) {
                                    var thisSubCat = $.trim(subCategories[subCat]).toLowerCase();

                                    // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                    if(!sortedSchools.hasOwnProperty(thisSubCat)) {
                                        // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                        sortedSchools[thisSubCat] = [schoolList[i]];
                                    } else {
                                        // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                        sortedSchools[thisSubCat].push(schoolList[i]);
                                    }
                                }
                            }

                            // SINGLE TYPE VALUE
                            else {
                                // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                if(!sortedSchools.hasOwnProperty(thisCategory)) {
                                    // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                    sortedSchools[thisCategory] = [schoolList[i]];
                                } else {
                                    // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                    sortedSchools[thisCategory].push(schoolList[i]);
                                }
                            }
                        }
                    }

                    // NOW SORT THE SCHOOLS ALPHABETICALLY WITHIN EACH CATEGORY
                    $.each(sortedSchools, function(category, schools) {
                        schools.sort(function(a, b) {
                            if($.trim(a.schoolName).toLowerCase() < $.trim(b.schoolName).toLowerCase()) {
                                return -1;
                            }
                            if($.trim(a.schoolName).toLowerCase() > $.trim(b.schoolName).toLowerCase()) {
                                return 1;
                            }
                            return 0;
                        });
                    });

                    // NOW SORT THE SCHOOLS BY TYPE
                    var expectedTypeOrder = ["high", "middle", "elementary", "alternative"];
                    $.each(sortedSchools, function(category, schools) {
                        schools.sort(function(a, b) {
                            if(expectedTypeOrder.indexOf(a.schoolType.toLowerCase()) < expectedTypeOrder.indexOf(b.schoolType.toLowerCase())) {
                                return -1;
                            }
                            if(expectedTypeOrder.indexOf(a.schoolType.toLowerCase()) > expectedTypeOrder.indexOf(b.schoolType.toLowerCase())) {
                                return 1;
                            }
                            return 0;
                        });
                    });

                    // FINALLY BUILD THE SCHOOL LIST
                    var completedSchoolList = '';
                    $.each(sortedSchools, function(category, schools) {
                        switch(category) {
                            case "falcon zone":
                                completedSchoolList += '
Falcon Zone';
                            break;
                            case "power zone":
                                completedSchoolList += '
POWER Zone';
                            break;
                            case "sand creek zone":
                                completedSchoolList += '
Sand Creek Zone';
                            break;
                            case "iconnect zone":
                                completedSchoolList += '
iConnect Zone';
                            break;
                        }

                        $.each(schools, function(index, school) {
                            completedSchoolList += '
' + school.schoolName + '';
                        });
                    });

                    // INSERT THE NEW SCHOOL LIST
                    $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html(completedSchoolList);

                    // ADD SCHOOL LIST
                    var schoolDropdown =	'
' +
                                                '
Our Schools' +
                                                '
' +
                                                    '' + $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() + '' +
                                                '' +
                                            '';

                    // ADD SCHOOL LIST TO THE DOM	
                    $(".mystart-left-inner.left").append(schoolDropdown);

                    // BIND DROPDOWN EVENTS
                    _this.DropdownActions({
                        "dropdownParent": ".cs-mystart-dropdown.schools",
                        "dropdownSelector": ".cs-selector",
                        "dropdown": ".cs-dropdown",
                        "dropdownList": ".cs-dropdown-list"
                    });

                    var csMobileSchoolsAdded = false;
                    setMobileSchools();
                    $(window).load(function() { setMobileSchools(); });
                    $(window).resize(function() { setMobileSchools(); });

                    function setMobileSchools() {
                        if(!csMobileSchoolsAdded) {
                                if($('#rs-menu .rs-menu-group.schools').length) {
                                    $('.rs-menu-group.schools > .rs-menu-group-list > ul').html(completedSchoolList);
                                    $('.rs-menu-group.schools > .rs-menu-group-list > ul a').attr("role", "menuitem");
                                    csMobileSchoolsAdded = true;
                                }
                        }
                    }
                }
            });
		},

