        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            if($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1440,
                    "imageHeight" : 900,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [],  // ["image", "title", "overlay"]
                    "playPauseControl" : false,
                    "backNextControls" : false,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade",
                    "transitionSpeed" : 2,
                    "transitionDelay" : 6,
                    "fullScreenRotator" : true,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {
                    	if(false == true) {
                            if($("#hp-slideshow .mmg-slide.active .mmg-description").length) {
                                if(!$("#hp-slideshow .mmg-description-inner-inner .mmg-bullets-outer").length) {
                                    $("#hp-slideshow .mmg-bullets-outer").appendTo("#hp-slideshow .mmg-description-inner-inner");
                                }
                            } else {
                                $('


').appendTo("#hp-slideshow .mmg-slide.active");
                                $("#hp-slideshow .mmg-bullets-outer").appendTo("#hp-slideshow .mmg-description-inner-inner");
                            }

                            if(!$("#hp-slideshow .mmg-bullets-outer .mmg-control.play-pause").length) {
                                $("#hp-slideshow .mmg-control.play-pause").prependTo("#hp-slideshow .mmg-bullets-outer");
                            }
                        }
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {
                    	$("#hp-slideshow .mmg-description").wrapInner("
");
                        $("#hp-slideshow .mmg-description-inner").wrapInner("
");

                        if(false == true) {
                            if($("#hp-slideshow .mmg-slide.active .mmg-description").length) {
                                if(!$("#hp-slideshow .mmg-description-inner-inner .mmg-bullets-outer").length) {
                                    $("#hp-slideshow .mmg-bullets-outer").appendTo("#hp-slideshow .mmg-description-inner-inner");
                                }
                            } else {
                                $('


').appendTo("#hp-slideshow .mmg-slide.active");
                                $("#hp-slideshow .mmg-bullets-outer").appendTo("#hp-slideshow .mmg-description-inner-inner");
                            }

                            if(!$("#hp-slideshow .mmg-bullets-outer .mmg-control.play-pause").length) {
                                $("#hp-slideshow .mmg-control.play-pause").prependTo("#hp-slideshow .mmg-bullets-outer");
                            }
                        }

                        if($(window).width() > 1023) {
                        	var slideshowHeight = $(window).height();
                            slideshowHeight = slideshowHeight - 44;
                            $("#hp-slideshow-outer").css({
                                "height": slideshowHeight + "px"
                            });
                        }
                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {
                    	if($(window).width() > 1023) {
                            var slideshowHeight = $(window).height();
                            slideshowHeight = slideshowHeight - 44;
                            $("#hp-slideshow-outer").css({
                                "height": slideshowHeight + "px"
                            });
                        }
                    } // props.element, props.mmgRecords
                });
            }
        },

        "StreamingVideo": function() {
        	// FOR SCOPE
            var _this = this;

        	if("Streaming Video" == "Streaming Video") {

            	$("#sw-content-container10.ui-hp").empty();

            	var videoVendor = ("YouTube" == "YouTube;Vimeo") ? "youtube" : "YouTube";
				var mobilePhoto = "/cms/lib/CO01900838/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg";

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                	mobilePhoto = "/cms/lib/CO01900838/Centricity/Template/17/streaming-video-default.jpg";
                }

                $("#sw-content-container10.region.ui-hp").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": "KLvhSrTHyJk", // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [960], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : false,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 640, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function() {
                    	if(false) {
                        	$("#hp-slideshow .cs-fullscreen-video-buttons").append($("#hp-slideshow .cs-fullscreen-video-button.unmute-button")).append($("#hp-slideshow .cs-fullscreen-video-button.mute-button"));
                            $("#hp-slideshow .cs-fullscreen-video-button.mute-button").attr("aria-label", "Mute Background Video");
                        	$("#hp-slideshow .cs-fullscreen-video-button.unmute-button").attr("aria-label", "Unmute Background Video");
                        } else {
                        	$("#hp-slideshow .cs-fullscreen-video-button.unmute-button, #hp-slideshow .cs-fullscreen-video-button.mute-button").remove();
                        }

                        $("#hp-slideshow .cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");

                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                        	if(_this.A11yClick(e)) {
                            	$("#hp-slideshow .cs-fullscreen-video-button.pause-button").focus();
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                        	if(_this.A11yClick(e)) {
                            	$("#hp-slideshow .cs-fullscreen-video-button.play-button").focus();
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.mute-button", function(e) {
                        	if(_this.A11yClick(e)) {
                            	$("#hp-slideshow .cs-fullscreen-video-button.unmute-button").focus();
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.unmute-button", function(e) {
                        	if(_this.A11yClick(e)) {
                            	$("#hp-slideshow .cs-fullscreen-video-button.mute-button").focus();
                            }
                        });
                    },
                    "onStateChange" : function() { }
                });

                // ADD DESCRIPTION
                var showVideoTitle = true;
                var showVideoCaption = true;
                var showVideoLink = true;
                if(showVideoTitle || showVideoCaption || showVideoLink) {
                	$('
' +
                    	'
' +
                        	'
' +
                            	((showVideoTitle) ? "
Reach Peak Performance with School District 49" : "") +
                            	((showVideoCaption) ? "
The Best Choice to Learn, Work and Lead. Fastest growing district in Colorado, serving 133 square miles and 25,000 students.  " : "") +
                                ((showVideoLink) ? "
Discover More" : "") +
                            '' +
                    	'' +
                    '').insertAfter($("#hp-slideshow #cs-fullscreen-video-outer"));

                    $("#hp-slideshow .cs-fullscreen-video-buttons").addClass("displayFlexRow");
                    $("#hp-slideshow .cs-fullscreen-video-overlay-inner").append($("#hp-slideshow .cs-fullscreen-video-buttons"));
                } else {
                	$('
' +
                    	'
' +
                        	'
' +
                            '' +
                    	'' +
                    '').insertAfter($("#hp-slideshow #cs-fullscreen-video-outer"));
                    $("#hp-slideshow .cs-fullscreen-video-buttons").addClass("displayFlexRow");
                    $("#hp-slideshow .cs-fullscreen-video-overlay-inner").append($("#hp-slideshow .cs-fullscreen-video-buttons"));
