	$(document).ready(function() {

		var date = new Date();
		var d = date.getDate();
		var m = date.getMonth();
		var y = date.getFullYear();

		$('#calendar').fullCalendar({
			editable: false,
			header: {
					left: 'prev,next today',
					center: 'title',
					right: 'month,agendaWeek,agendaDay'
			},
			eventClick: function(event) {
				if (event.url) {
					window.open(event.url);
					return false;
				}
    		},
			eventClick: function(event) {
				if (event.url) {
					window.location.href = event.url;
					return false;
				}
			},
			events: [
			]

		});

	});

