$('#630001 .level1 > li a').each(function() {



if ( $('#630001 .level1 > li a').children().length > 0 ) {



    $(this).attr('data-cat',$(this).children("span").text().substring(0,1));
var dataCat = $(this).attr('data-cat');
    $(this).closest("li").attr('data-cat', dataCat);

}

else {

var dataCat = $(this).attr('data-cat');
    $(this).closest("li").attr('data-cat', dataCat);

}
});


$(function(){
    var $lists = $('#630001 .level1 li'),
    years = [];

    $lists.each(function() {
        var year = this.getAttribute('data-cat');
        if ( ! ~$.inArray(year, years) ) {
            years.push(year);
      
        }
    });


    $.each(years, function (i, year) {
        $lists.filter('[data-cat="' + year + '"]').wrapAll('');
        $lists.filter('[data-cat="' + year + '"]').eq(0).before('
'+ year +'');
        $('.level1 ul.class-cat').attr('data-cat', year);
        console.log('working')
    });


    $('.class-cat').each(function(){
        $(this).find('.level1 li ').wrapAll('');
    });

});



$(document).ready(function(){
	



$('#630001 .level1 ul.class-cat').each(function(){

var dataCat = $(this).children().find('a').attr('data-cat');
$(this).attr('data-cat', dataCat);



});


var alphabeticallyOrderedDivs = $('#630001 .level1 .class-cat').sort(function(a, b) {
			return String.prototype.localeCompare.call($(a).data('cat').toLowerCase(), $(b).data('cat').toLowerCase());
		});

var container = $("#630001 .level1");
container.empty().append(alphabeticallyOrderedDivs);

});

