div.script:
               $(document).ready(function () {

                   var original = '';
                   // set hover
                   $('#RCNavigationContainer > DIV  > DIV.navLink img').hover(
			        function () {
			            original = $(this).attr('src');
			            if ($(this).attr('src').indexOf('-active.png') < 0) {
			                $(this).attr('src', $(this).attr('src').replace('.png', '-active.png'));
			            }
			        },
			        function () {
			            $(this).attr('src', original);
			        });

               });

