			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		$("#sw-mystart-search").appendTo($("#gb-search"));
		$(".gb-mystart-left").prepend($("#sw-mystart-left").html());
		$("#sw-mystart-right .sw-mystart-button, #sw-mystart-account").prependTo($(".gb-mystart-right"));
		$(".gb-mystart-right .clear").remove();
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("SELECT A SCHOOL");
		$("#sw-mystart-search").unbind();
		$(".sw-mystart-search").click(function(e){
			e.preventDefault();
			$("#gb-search-outer").slideToggle(500);
		});

		// ********** HEADER ********** //
		// AJAX ALERT
		if($(".sp").length) {
		    $.ajax({   
		        url : "/danville",
		        type : "GET",
		        success : function(data) {
				$("#gb-emergency-outer").append($(data).find("#gb-emergency"));
				if($("#gb-emergency div.ui-widget.app div.ui-widget-detail .ui-article").length != 0) {
					$("#gb-emergency-outer").toggle();
				}
		        }

		    });
		} else {
			if($("#gb-emergency div.ui-widget.app div.ui-widget-detail .ui-article").length != 0) {
				$("#gb-emergency-outer").toggle();
			}
		}	

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		$(".gb-channel-list-mystart").prepend($(".gb-mystart-left").html());
		$(".hp-to-content").on("click touchend", function(){
			$("html, body").animate({
				scrollTop: $("#hp-content-wrapper").offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});	
		});
		if($(".hp").length) {
			if ($(window).scrollTop() <= 60) {
				$("#gb-channel-list").addClass("channel-drop-up");
			} else {
				$("#gb-channel-list").removeClass("channel-drop-up");
			}
		}
		var channelLoc = $("#gb-channel-list").offset().top;
		$(window).scroll(function() {
			if($(window).width() > 768) {
				if($(".hp").length) {
					if($(window).scrollTop() >= channelLoc) {
						$('#gb-channel-list').css({ 'position': 'fixed', 'top': 0, 'background': '#333' });
					} else if($(window).scrollTop() < channelLoc) {
						$('#gb-channel-list').css({ 'position': 'absolute', 'top': 'auto', 'background': 'rgba(0,0,0,0.7)' });
					}
				} else {
					if($(window).scrollTop() >= channelLoc) {
						$('#gb-channel-list').css({ 'position': 'fixed', 'top': 0, 'background': '#333' });
						$("#gb-channel-list-outer").css("min-height", "0");
					} else if($(window).scrollTop() < channelLoc) {
						$('#gb-channel-list').css({ 'position': 'absolute', 'top': 'auto', 'background': 'rgba(0,0,0,0.7)' });
						$("#gb-channel-list-outer").css("min-height", "60px");
					}
				}
			}	
			// SET CLASS TO MAKE CHANNEL DROPDOWNS DROP UP IF SCROLL POSITION IS LESS THAN 40 PIXELS FROM TOP EDGE OF WINDOW
			if($(".hp").length) {
				if ($(window).scrollTop() <= 60) {
					$("#gb-channel-list").addClass("channel-drop-up");
				} else {
					$("#gb-channel-list").removeClass("channel-drop-up");
				}
			}
		});

		$("#view-full-site").on("click", function() {
			console.log("click");
			if($(this).hasClass("not-full-site")) {
				console.log("full");
				if($(".hp").length) {
					$('#gb-channel-list').css({ 'position': 'absolute', 'top': 'auto', 'background': 'rgba(0,0,0,0.7)' });
				} else {
					$('#gb-channel-list').css({ 'position': 'absolute', 'top': 'auto', 'background': 'rgba(0,0,0,0.7)' });
					$("#gb-channel-list-outer").css("min-height", "60px");
				}		
			}
		});

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").html("VIEW ALL ");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//DOC add read full story button to each healine
        	$(".ui-widget.app.headlines:not('.cs-featured-headlines')").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(".ui-article-description", this).append('READ MORE');
        		});
        	});

        	if($(".call-action-box.false").length == 3) {
			$("#hp-boxes").hide();
		} 

        	if($(".hp-content-inner .ui-hp > div").length == 0) {
			$(".hp.column.one").hide();
		}
		if(!$("#hp-content div.ui-widget.app").length) {
			$("#hp-content").hide();
		}

        	//MOD EVENTS
		$(".upcomingevents").modEvents({
			columns     : "yes",
			monthLong   : "no",
			dayWeek     : "yes"
		});
		eventsByDay(".upcomingevents .ui-articles");
		$(".upcoming-column.left .joel-date").each(function(){
			if($(this).text().length < 2) {
				$(this).text("0"+$(this).text());	
			}	
		});
		$(".view-calendar-link").html("VIEW FULL CALENDAR ");
		$(".gb-scroll-top").on("click touchend", function(){
			$("html, body").animate({
				scrollTop: 0
			}, 500, function(){
				$("html, body").stop(true, false);
			});	
		});

		// ********** FOOTER ********** //
		$("#sw-footer-links li").eq(1).appendTo($("#sw-footer-links ul"));
		$("#sw-footer-links li").eq(2).html($("#sw-footer-links li:eq(2) a"));
		if($(".contact-email a").text() == ""){
			$(".contact-email").hide();
		}
	}

	function boxSizes() {
	        //DOC same hight for call to action boxes
        	if($(".call-action-box.false").length == 3) {
			$("#hp-boxes").hide();
		} else {
	        	var boxHeight = 0;
			$(".call-action-box").each(function() {
				if($(this).outerHeight() > boxHeight){
					if($("img", this).height() != 0) {
						boxHeight = $(this).outerHeight();
					} else {
						boxHeight = $(this).outerHeight()+50;
					}
				}
			});
			$(".call-action-box").height(boxHeight);
		}
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "What would you like to search for?");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "What would you like to search for?") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("What would you like to search for?");
			}
		});
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});
		$(".upcomingevents .ui-article-description", container).each(function(){
				$(".upcomingevents .sw-calendar-block-title", this).prependTo($(this));
		});
	}

	function calendarListView() {
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		var getWidth = $(window).width();
		if(getWidth >= 768){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth <= 640){ // 640-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$("a#ui-btn-calendarlist-list").click();
		}
	}

