jQuery( document ).ready(function( $ ) {
	$("li.active").removeClass('active');

	var isSlideshowRunning = true;
	$('.slider-pro').attr("tabindex", "0");
	$('#flags ul li a').attr("tabindex", "0");
	$('.search-field').attr("tabindex", "0");

	$(".sp-buttons").append("
 Left Play PauseRight  ");

	$('.slider-pro').on( 'keydown', function( event ) {
		if ( event.which === 37 ) {
			event.preventDefault();
			$('.slider-pro').sliderPro('previousSlide');
			return false;
		} else if ( event.which === 39 ) {
			event.preventDefault();
			$('.slider-pro').sliderPro('nextSlide');
			return false;
		} else if ( event.which === 32 || event.which === 13 ) {
			event.preventDefault();
			var slider = $( '.slider-pro' ).data( 'sliderPro' );
			if(isSlideshowRunning) {
				stopSlideShow();
			} else {
				startSlideShow();				
			}
			return false;
		}
	});

	function stopSlideShow() {
		var slider = $( '.slider-pro' ).data( 'sliderPro' );
		slider.settings.autoplayOnHover = "stop";
		slider.stopAutoplay();				
		isSlideshowRunning = false;
		$('#play-button').show();
		$('#play-button').focus();
		$('#pause-button').hide();
	}

	function startSlideShow() {
		$('.slider-pro').sliderPro('nextSlide');

		var slider = $( '.slider-pro' ).data( 'sliderPro' );
		slider.settings.autoplayOnHover = "stop";
		slider.startAutoplay();				
		isSlideshowRunning = true;
		$('#pause-button').show();
		$('#pause-button').focus();
		$('#play-button').hide();		
	}

	$('#left-button').on( 'keydown', function( event ) {
		if ( event.which === 32 || event.which === 13 ) {
			event.preventDefault();
			$('.slider-pro').sliderPro('previousSlide');
			return false;			
		}
	});

	$('#right-button').on( 'keydown', function( event ) {
		if ( event.which === 32 || event.which === 13 ) {
			event.preventDefault();
			$('.slider-pro').sliderPro('nextSlide');
			return false;			
		}
	});

	$('#left-button').click(function(event) {
		event.preventDefault();
		$('.slider-pro').sliderPro('previousSlide');
		return false;
	});

	$('#right-button').click(function(event) {
		event.preventDefault();
		$('.slider-pro').sliderPro('nextSlide');
		return false;
	});

	$('#play-button').click(function(event) {
		event.preventDefault();
		startSlideShow();
		return false;
	});

	$('#pause-button').click(function(event) {
		event.preventDefault();
		stopSlideShow();
		return false;
	});
});
