    var csTemplate = {};

    var CreativeTemplate = {

        /*****
            PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "",
        "MobileMenuBreakpoint": 768,



        /*****
            METHODS
        *****/

// BIG BANG METHOD
        "Init": function() {
            // FOR SCOPE
            window.csTemplate = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.SLM(); //will daisy chain the RsMenu function
            this.GlobalIcons();
            this.mobileSchoolsClick();
            this.MyStart();
            this.Search();
            this.Header();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Body();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.CheckSlideshow();
            this.AppAccordion();
            this.Footer();
            this.BuildStylesheet();

            $(window).load(function(){ window.csTemplate.WindowLoad(); });
            $(window).resize(function(){ 
                 window.csTemplate.WindowResize(); 
                 window.csTemplate.CalendarListView();
                 window.csTemplate.CheckSlideshow();
                 window.csTemplate.StickyChannelBar();
            });
            $(window).scroll(function(){ window.csTemplate.CheckSlideshow(); window.csTemplate.StickyChannelBar(); });
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            if($(window).width() >= 320 && $(window).width() <= 639){
                this.CalendarListView();
            }    
            this.Search();

            $("div.ui-widget.app.navigation li div.bullet").click(function(){
                return true;
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-mystart-dropdown.schoollist").unbind("hover");
            $(".sw-mystart-dropdown.schoollist").hover(function(){

                },function(){
                $(".sw-dropdown-arrow",this).removeClass("open");
                $(this).removeClass("open");
                $(".sw-dropdown", this).slideUp(300, "swing").attr("aria-hidden", "true");
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-mystart-dropdown.translate").unbind('hover');
            $(".sw-mystart-dropdown.translate").hover(function(e){

            }, function(e) {
                //e.stopPropagation();
                $(".sw-dropdown-arrow").removeClass("rotate-arrow");
                $(this).removeClass("open");
                $(".sw-dropdown").slideUp(300, "swing").attr("aria-hidden", "true");
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $("#sw-mystart-account").unbind('hover');
            $("#sw-mystart-account").hover(function(){

            }, function() {
                $(".icon-font-arrow",this).removeClass("open");
                $("#cs-sw-myaccount-list").slideUp(100, "swing").attr("aria-hidden", "true");
                $("#sw-myaccount", this).attr("aria-expanded","false");
                $(this).removeClass("clicked-state");
            });



            /// CHANNEL HOVER FUNCTION 
            channelHoverTimer = "";
            $(".sw-channel-item").unbind('hover');
            $('.sw-channel-item.mega-channel').hover(function(e){ //will move the arrow around on hover
            	$(".sw-channel-item ul").stop(true, true);
                var channelItem = $(this);

                //adjust triangle to center
                var leftPos = $(this).position().left; //get the left position of the hovered element
                var elementSize = $(this).width();

                $(this).append($('.mega-menu-arrow'));

                channelHoverTimer = setTimeout(function(){
                    var subList = channelItem.children('ul');

                    if ($.trim(subList.html()) !== "") {
                        subList.slideDown(300, "swing");

                      var elm = $(subList);
                      console.log(elm);
                      var off = elm.offset();
                      var l = off.left;
                      var w = elm.width();
                      var docH = $("#sw-channel-list-container").height();
                      var docW = $("#sw-channel-list-container").width();
                      var winW = $(window).width();
                      var overflow = -((l + w) - winW + 80);
                      var isEntirelyVisible = (l + w <= winW);
                      if (!isEntirelyVisible) {
                          channelItem.addClass('edge');
                          $(".mega-channel.sw-channel-item.edge ul.sw-channel-dropdown").css("left", overflow);
                      } else {
                          $(this).removeClass('edge');
                      }

                    }
                    channelItem.addClass("hover");
                }, 500);

            },
            function(e){
            	$(".sw-channel-item ul").stop(true, true);
                //hide arrow
                $('.mega-menu-arrow').hide();
                clearTimeout(channelHoverTimer);
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },

//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    this.MegaMenu();
                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "A11yClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

          // 
        "BuildStylesheet": function() {

            var globalIconColor80 = "rgba(" + this.HexToRgb('#2e56a5') + ", .8)";
            var galleryDescColor80 = "rgba(" + this.HexToRgb('#2e56a5') + ", .8)";
            var lmButtonColor20 = "rgba(" + this.HexToRgb('#FFFFFF') + ", .2)";
            var regBBGColor80 = "rgba(" + this.HexToRgb('#2e56a5') + ", .8)";
            var ssBtnColor30 = "rgba(" + this.HexToRgb('') + ", .3)";
            var regBGTextColor20 = "rgba(" + this.HexToRgb('#FFFFFF') + ", .2)";

            var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var dynStyles = '#gb-global-icons .global-icon {' +
                                  	'	background: ' + globalIconColor80 + ';' +
                                  '}' + 
                                  '#hp-slideshow .rs-gallery-container .rs-gallery-desc {' +
                                  	'	background-color: ' + galleryDescColor80 + ';' +
                                  '}' + 
                                  '#hp-slideshow .rs-gallery-container .rs-gallery-desc .rs-gallery-desc-more-link a {' +  
                                  	'border: 1px solid ' + lmButtonColor20 + ';' +
                                    '}' + 
                                  '#hp-slideshow .rs-gallery-container .rs-gallery-control.back:after {' +
                                  	'	border: 2px solid ' + ssBtnColor30 + ';' +
                                    '}' + 
                                  '#hp-slideshow .rs-gallery-container .rs-gallery-control.next:after {' +
                                  	'	border: 2px solid ' + ssBtnColor30 + ';' +
                                    '}' + 
                                  '#hp-content-bottom .ui-widget.app.headlines .ui-article-thumb .img img {' +
                                  	'	border: 1px solid ' + regBGTextColor20 + ';' +
                                  '};'

                var rules = document.createTextNode(dynStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = dynStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },
        "HexToRgb": function(hex){
            var r = parseInt((this.CutHex(hex)).substring(0,2),16);
            var g = parseInt((this.CutHex(hex)).substring(2,4),16);
            var b = parseInt((this.CutHex(hex)).substring(4,6),16);
            return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
            return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


//TEMPLATE METHODS
        "MyStart": function() {

        	var template = this;

            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            $("#gb-search-outer #sw-mystart-search").prepend('
I\'\m looking for...');

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search");

            //REPLACE MYSTART DROPDOWN ARROWS
            $(".sw-myaccount-chevron").text("▼");
            $(".sw-dropdown-arrow").text("▼");

            //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
            $(".sw-mystart-dropdown").addClass("transition");

            //CHANGE THE SCHOOLLIST TEXT
            $(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

            //ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
            $(".sw-mystart-button").addClass("transition");

            //APPEND MYSTART ITEMS TO APPROPRIATE PLACES

            var mystartBtns = "";
            if($(".sw-mystart-button.register").length){
                mystartBtns += "
" + $(".sw-mystart-button.register").html() + "";
            }
            mystartBtns += "
" + $(".sw-mystart-button.signin").html() + "";
            $(".gb-mystart").prepend('

Account' + mystartBtns + '');



            //$('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#gb-school-dropdown ul');

            $('#sw-mystart-left .sw-mystart-button.home').prependTo('.gb-mystart');
            $('.gb-mystart .sw-mystart-button.home a').html('MSBSD HOME');

            this.Translator();

            $(".sw-mystart-dropdown.translate").each(function(i) {
                // SET ATTRIBUTES
                $(this).attr({"role": "button", "aria-expanded": "false", "aria-haspopup": "true"});
                $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
                $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
                $(".sw-dropdown", this).attr("aria-hidden", "true");
                $(".sw-dropdown-list li", this).attr("role", "menuitem");
            });

            //DOC ADD CUSTOM CLICK EVENT FOR TRANSLATE DROPDOWN
            $(".sw-mystart-dropdown.translate").off();
            $(".sw-mystart-dropdown.translate").click(function(){
                $(this).toggleClass("open").attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
                $(".sw-dropdown-arrow",this).toggleClass("open");
                $(".sw-dropdown", this).slideToggle(300, "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
            });

            //$(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a").attr("tabindex", "0");

            $(".sw-mystart-dropdown.translate").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        //e.preventDefault();
                        		$(".sw-dropdown", this).slideToggle(300, "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                          if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                              $(this).addClass("open").attr("aria-expanded","true");
                              $(".sw-dropdown-list li:first-child a", this).focus();
                          } else {
                                $(this).removeClass("open").attr("aria-expanded","false");
                          }
                          $(".sw-dropdown-list li a", this).attr("tabindex","0");
                        // FOCUS FIRST ITEM

                    break;
                }
            });

            $(".sw-mystart-dropdown.translate .sw-dropdown-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).parent().parent().parent().slideUp(300, "swing").attr("aria-hidden", "true");
                        $(".sw-mystart-dropdown.translate").removeClass("open").attr("aria-expanded","false").focus();

                }
            });

            $("#sw-myaccount").attr({"role": "button", "aria-expanded": "false", "aria-haspopup": "true"});

            //DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
            $("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
            $("#sw-mystart-account").off();
            $("#sw-mystart-account").click(function(){
                $(".icon-font-arrow",this).toggleClass("open");
                $("#cs-sw-myaccount-list").slideToggle(100, "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list", this).attr("aria-hidden") == "true") ? "false" : "true");
                $("#sw-myaccount", this).attr("aria-expanded", ($("#sw-myaccount", this).attr("aria-expanded") == "true") ? "false" : "true");
            });

            $("#cs-sw-myaccount-list li").each(function(){
                $("> a", this).attr("tabindex", "0"); 
            }); 

            $("#sw-myaccount").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();


                        $(this).next("#cs-sw-myaccount-list").slideToggle(100, "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                        if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                            $(this).addClass("open").attr("aria-expanded","true");
                            $(this).next("#cs-sw-myaccount-list").find("li:first-child a").focus();
                        } else {
                            $(this).removeClass("open").attr("aria-expanded","false");
                        }
                        // FOCUS FIRST ITEM

                    break;
                }
            });    

            $("#cs-sw-myaccount-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).parent().parent().slideUp(100, "swing").attr("aria-hidden", "true");
                        $("#sw-myaccount").removeClass("open").attr("aria-expanded","false").focus();
                        $("#sw-mystart-account").removeClass("clicked-state");
                    break;
                }
            });
        },
        "Header": function() {

            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/AK01000953/Centricity/Template/GlobalAssets/images///logos/matsu text.png");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('');
            var siteNameTwo = jQuery.trim('');
            var showLogoOnly = jQuery.trim('true');
            if(showLogoOnly == "false") {
                if((siteNameOne == "") && (siteNameTwo == "")) {
                    var splitLen = 2;
                    var siteName = "Matanuska-Susitna Borough School District";
                    siteName = siteName.split(" ");
                    var siteNameLength = siteName.length;
                    if(siteNameLength > 2){
                        siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                    } else {
                        siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                    }
                    siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                    $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
                } else if((siteNameOne != "") && (siteNameTwo == "")) {
                    $("#gb-sitename").prepend("
" + siteNameOne + "");
                } else if((siteNameOne == "") && (siteNameTwo != "")) {
                    $("#gb-sitename").prepend("
" + siteNameTwo + "");
                } else if((siteNameOne != "") && (siteNameTwo != "")) {
                    $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
                }
           }     
        },
        "ChannelBar": function() {
            //USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //ADD FIRST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").first().addClass("first");
            //ADD LAST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").last().addClass("last");

            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
            $("ul.sw-channel-list li.sw-channel-item").addClass("transition");

            $('#channel-navigation > .sw-channel-item > a').not('#channel-navigation > .sw-channel-item.home > a').click(function(e){
            	e.preventDefault();
            });
        },
         "StickyChannelBar": function() {
            var channelHeight = $("#gb-channel-list-outer").height();

            if ($(window).scrollTop() <= channelHeight) {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
            } else {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", channelHeight + "px");
                    }
                }
            }
        },
