            $(document).on("click keydown", "#gb-schools-btn", function(e) {
                if(_this.A11yClick(e)) {    
                    e.preventDefault();
                    if($(this).hasClass("active")) {
                        $(this).removeClass("active")
                        $("#gb-school-list").fadeOut( "slow", "linear" );
                        $(this).focus().attr("aria-expanded", "false");
                        $(".gb-school-list-section").removeClass("active");
                        $(".gb-school-list-header").removeClass("active");

                    } else {
                        $(this).addClass("active").attr("aria-expanded", "true");
                        $("#gb-school-list").fadeIn( "slow", "linear" );
                        $(".gb-school-list-header.elem").focus().addClass("active");
                        $(".gb-school-list-section.elem").addClass("active");
                    }
                }
            });

            $(document).on("click keydown", "#gb-school-list-close", function(e) {
                if(_this.A11yClick(e)) {    
                    e.preventDefault();
                    $(this).removeClass("active");
                    $("#gb-school-list").fadeOut( "slow", "linear" );
                    $("#gb-schools-btn").removeClass("active").attr("aria-expanded", "false").focus();
                    $(".gb-school-list-section").removeClass("active");
                    $(".gb-school-list-header").removeClass("active");
                }
            });


