        "Search": function() {
            //CUSTOM MYSTART SEARCH PLACEHOLDER
            var defText = "Type and press Enter to search our website.";

            var searchBox = $('#swsitesearch-txt-searchstring');

            //if( $('#search-text-label').length <= 0 ){
            //	searchBox.after('Search');
            //}
            searchBox
            	.attr('tabindex', -1)
            	.attr('aria-hidden', 'true')
                .attr("aria-label", "Search input")
                .attr("placeholder", '')
                .val(defText)
                .focus(function() {
                    if($(this).val() == defText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defText);
                    }
                });

            $("#gb-search-btn, #gb-mobile-search-btn").off().click(function(e){
            	e.preventDefault();
                if(!$("#gb-search-outer").hasClass("open")){
                	$("#gb-search-outer").addClass("open");
                	$("#gb-search-outer").fadeIn( "slow", "linear" );
                    if( $('.ios').length <= 0 ){
                        setTimeout(function(){
                            $('#swsitesearch-txt-searchstring').focus();
                        }, 1000);
                    }
                    $(this).attr("aria-expanded","true");
                    $("#gb-search-outer #swsitesearch-txt-searchstring").attr("tabindex","0");
                } else {
                	$("#gb-search-outer").removeClass("open");
                	$("#gb-search-outer").fadeOut( "slow", "linear" );
                    $("#gb-search-btn").attr("aria-expanded","false").focus();
                    $("#gb-search-outer #swsitesearch-txt-searchstring").attr("tabindex","-1");
                }
            });
            /*$("#gb-search-btn").keydown(function(e){
                if(e.keyCode == this.KeyCodes.enter || e.keyCode == this.KeyCodes.space) {
                    e.preventDefault();
                    if(!$("#gb-search-outer").hasClass("open")){
                        $("#gb-search-outer").addClass("open");
                        $("#gb-search-outer").fadeIn( "slow", "linear" );
                        $("#gb-search-outer #cs-custom-search-input").focus();
                    } else {
                        $("#gb-search-outer").removeClass("open");
                        $("#gb-search-outer").fadeOut( "slow", "linear" );
                        $("#gb-search-btn").focus();
                    }
                }
            });*/

            //SEARCH FORM SUBMISSION - added by Matt B - case#3088630 - 05.21.18
            $("#gb-search-submit").click(function(e){
                e.preventDefault();

                if($("#swsitesearch-txt-searchstring").val() != "Type and press Enter to search our website.") {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#swsitesearch-txt-searchstring").val();
                }
            });

