                            $("#gb-mobile-translate-outer").append(translateAdditional);
                        break;
                    }
                }
        },
        "AppAccordion": function(){
            //DOC dont add app into accordion if it doesnt have a header
            $("#hp-content-middle").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });

            $(".sp.column.one").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });
        },


// APP METHODS
        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": 
                    case "768": 
                    case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": 
                    case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },
        "UpcomingEvents": function(){

        	var _this = this;

            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
                var countLi = 0;
                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                    if($('.upcoming-column.left h1', this).length){
                        var newDateTime = $('.upcoming-column.left h1', this).html().split("");
                        var newDate = '';

                        newDate += newDateTime[0];
                    } 

                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend(newDate + 'ALL DAY');
                        } else {
                            $(this).prepend(newDate);
                        }
                    }); 

                });
                //ADD THE MORE-LESS BUTTON IF THERE ARE MORE THAN 2 EVENTS ON A DAY
                $("#hp-content-top .ui-widget.app.upcomingevents .upcoming-column.right").each(function() {
                    if($(".ui-article-description", this).length > 2) {
                        $(this).append('More');
                    }
                });

                //CLICK FUNCTION FOR THE MORE-LESS BUTTON
                $(document).on("click keydown", ".hp-events-btn", function(e) {
                    if(_this.A11yClick(e)) {    
                        e.preventDefault();
                        if($(this).hasClass("active")) {
                            $(this).removeClass("active").attr("aria-expanded","false").siblings(".ui-article-description:nth-of-type(n+3)").slideUp(500);
                            $("span", this).eq(0).text("More");
                        } else {
                            $(this).addClass("active").attr("aria-expanded","true").siblings(".ui-article-description:nth-of-type(n+3)").slideDown(500);
                            $("span", this).eq(0).text("Less");
                        }
                    }
                });

                $(".ui-widget.app.upcomingevents .view-calendar-link").each(function(){
                	$(this).append('')
                });

                //WRAP THE VIEW CALENDAR LINK
                $("#hp-content-top .ui-widget.app.upcomingevents .view-calendar-link").wrap('
');

                //ADD THE 2 EXTRA CALENDAR LINKS
                var addCalendarLinks = "";
                var calendarLinks = [
                    ["one", "SCHOOL YEAR CALENDARS", "https://www.matsuk12.us/Page/14952", "true"],
                    ["two", "ASSESSMENT CALENDAR", "https://www.matsuk12.us/calendar/assessment", "true"]
                ];

                for(var i = 0; i < 2; i++) {
                    if(calendarLinks[i][1] != ""){
                        addCalendarLinks += '' + calendarLinks[i][1] + '';
                    }
                }
                $("#hp-content-top .ui-widget.app.upcomingevents .view-calendar-link-outer").prepend(addCalendarLinks);

                $("#hp-content-top .ui-widget.app.upcomingevents .calendar-link").wrapAll('
');

                //ADD PHOTO BACKGROUND
                var ueBGPhotoSrc = jQuery.trim("/cms/lib/AK01000953/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
                var photoSrcSplit = ueBGPhotoSrc.split("/");
                var photoSrcSplitLen = photoSrcSplit.length;
                if((ueBGPhotoSrc != "") && (photoSrcSplit[photoSrcSplitLen - 1] != "default-man.jpg")) {
                	$("#hp-content-top-outer").css("background", "url('"+ueBGPhotoSrc+"') center center / cover no-repeat fixed");
                } else {
                   	$("#hp-content-top-outer").css("background", "url('/cms/lib/AK01000953/Centricity/Template/10/images/default/ue-BG-photo-default.png') center center / cover no-repeat fixed");
                }

            }
        }
