$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=%2bIK8FiNI9P%2fDSyi%2bg0Nr6A%3d%3d&SessionID=44047a46-7c36-4be4-aa3e-b2632b09eb41&SiteID=1&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+8%3a31%3a59+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.6.124';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
