    $(document).ready(function () {
        //        var originalSize = $('div').css('font-size');
        //        var originalSizes = $('span').css('font-size');
        //        var originalSized = $('a').css('font-size');
        //        var originalSizel = $('li').css('font-size');
        //        // reset
        $(".resetMe").click(function () {
            //            $('div').css('font-size', originalSize);
            //            $('span').css('font-size', originalSizes);
            //            $('a').css('font-size', originalSized);
            //            $('li').css('font-size', originalSizel);

            location.reload();
        });

        // Increase Font Size
        $(".increase").click(function () {
            var currentSize = $('div').css('font-size');
            var currentSize = parseFloat(currentSize) * 1.2;
            $('div').css('font-size', currentSize);

            var currentSizes = $('span').css('font-size');
            var currentSizes = parseFloat(currentSizes) * 1.2;
            $('span').css('font-size', currentSizes);

            var currentSized = $('a').css('font-size');
            var currentSized = parseFloat(currentSized) * 1.2;
            $('a').css('font-size', currentSized);

            var currentSizel = $('li').css('font-size');
            var currentSizel = parseFloat(currentSizel) * 1.2;
            $('li').css('font-size', currentSizel);

            return false;
        });

        // Decrease Font Size
        $(".decrease").click(function () {
            var currentSize = $('div').css('font-size');
            var currentSize = parseFloat(currentSize) * 0.8;
            $('div').css('font-size', currentSize);

            var currentSizes = $('span').css('font-size');
            var currentSizes = parseFloat(currentSizes) * 0.8;
            $('span').css('font-size', currentSizes);

            var currentSized = $('a').css('font-size');
            var currentSized = parseFloat(currentSized) * 0.8;
            $('a').css('font-size', currentSized);

            var currentSizel = $('li').css('font-size');
            var currentSizel = parseFloat(currentSizel) * 0.8;
            $('li').css('font-size', currentSizel);

            return false;
        });
    });

input.script:
$( document ).ready(function() {
$('video').off('play').on('play', function() {
	var dd = this.id
	$('video').each(function( index ) {
		if(dd != this.id){
			this.pause();
		}
	});
});
});
