		                if (console && typeof console.log == "function") {
		                    console.log("Toolbar failed to load, retrying", err);
		                }
		                window.setTimeout(initializeToolbar, 300);
		            }
		        });
		    }
		}
		
		initializeToolbar();
	});
	
