function reShuffleEvents() {
  if (typeof dayEventsShuffle != undefined) {
	for (var j = 0; j < dayEventsShuffle.length; j++) {
		eval("new " + dayEventsShuffle[j]);
	}
  }
}

function setFormRepeatSave(element) {
	element.form['repeat_save_method'].value = element.id.replace("edit_repeat_save_method_", "");
}


function setFormRepeatDelete(element) {
	element.form['repeat_save_method'].value = element.id.replace("edit_repeat_save_method_", "");
}
function setFormModified(element) {
	element.form['modified'].value = 1;
}

function editEvent(eventID, theDate) {
	editEventReq.send("module=calendar&component=get_edit_event&event_id=" + eventID+"&dateEditing=" + theDate);
}



function changeCurrentDay(tdCell, theDate, type) {
	if (!tdCell) {
		return;
	}

  if (tdCell.id.search("-small")) {
		cellPrefix = "td-small-";
	} else {
		cellPrefix = "td";
	}

	cellCurrentDate = tdCell.id.replace(cellPrefix, "");

  // disable the previous
	var currentDateString = currentDate.format("%Y-%m-%d");

    if (new Date().format("%Y-%m-%d") == currentDate.format("%Y-%m-%d")) {
		if ($("td" + currentDateString) && currentViewMode == "month") { $("td" + currentDateString).className = "current-date"; }
		if ($("td-small-" + currentDateString)) { $("td-small-" + currentDateString).className = "current-date"; }
	} else {
		if ($("td" + currentDateString) && currentViewMode == "month") {
		  if (new Date().format("%Y-%m") != currentDate.format("%Y-%m")) {
			$("td" + currentDateString).className = "non-month-date";
		  } else {
			$("td" + currentDateString).className = "date";
		  }
		}
		if ($("td-small-" + currentDateString)) {
		  if (new Date().format("%Y-%m") != currentDate.format("%Y-%m")) {
			$("td-small-" + currentDateString).className = "non-month-date";
		  } else {
			$("td-small-" + currentDateString).className = "date";
		  }
		}
	}

  // enable the current one
	currentDate = currentDate.set('month', new Date().parse(theDate).format("%m") - 1);
	currentDate = currentDate.set('year', new Date().parse(theDate).format("%Y"));
	currentDate = currentDate.set('date', new Date().parse(theDate).format("%d"));

	if (new Date().format("%Y-%m-%d") != currentDate.format("%Y-%m-%d") && tdCell) {
		if ($("td" + theDate) && currentViewMode == "month") { $("td" + theDate).className = "selected-day"; }
				if ($("td-small-" + theDate)) { $("td-small-" + theDate).className = "selected-day"; }
			}

	if (!$("numo_calendar_large") && currentCommand != "moveNextBack") {
		//alert("full-reload"); 
				document.location.href="manage.numo?module=calendar&component=view&size=large&id=" + calendarID + "&viewMode=day&theDate="+theDate;
	    	} else {
		if (currentViewMode == "day") {
			reloadDay();
		} else if (currentViewMode == "week") {
			reloadWeek();
		}
	}
}

function deleteEvent(eventID) {
	if (confirm("Are you sure you wish to permanently delete this event?")) {
		if (currentViewMode == "month") {
			var monthToSend = currentDate.format("%Y-%m");
			req.send("module=calendar&component=get_month&id=" + calendarID + "&monthToLoad="+monthToSend+"&cmd=deleteEvent&event_id=" + eventID);
		} else if (currentViewMode == "day") {
			var dayToSend = currentDate.format("%Y-%m-%d");
			req.send("module=calendar&component=get_day&id=" + calendarID + "&dayToLoad="+dayToSend+"&cmd=deleteEvent&event_id=" + eventID);

		} else if (currentViewMode == "week") {
			var dayToSend = currentDate.format("%Y-%m-%d");
			req.send("module=calendar&component=get_week&id=" + calendarID + "&weekToLoad="+weekToSend+"&cmd=deleteEvent&event_id=" + eventID);
		}
	}
}

function saveEvent() {
	var eventContainer = "create-event-container";

	var myReveal = new Fx.Reveal($(eventContainer), {duration: 500, mode: 'vertical'});
	myReveal.dissolve().chain(function() {
	  var loadingContainer = "loading-container";

	  var myLoading = new Fx.Reveal($(loadingContainer), {duration: 500, mode: 'vertical'});
	  myLoading.reveal().chain(function() {

		  $('create-event-form').submit();
	  });
	});
}

function reloadCalendar(myCalendarID) {
	if (typeof myCalendarID == 'undefined') {
		myCalendarID = calendarID;
	}
	var monthToSend = currentDate.format("%Y-%m");
	req.send("module=calendar&component=get_month&id=" + myCalendarID + "&monthToLoad="+monthToSend);
}

function reloadWeek(myCalendarID) {
	if (typeof myCalendarID == 'undefined') {
		myCalendarID = calendarID;
	}
	var weekToSend = currentDate.format("%Y-%m-%d");
	req.send("module=calendar&component=get_week&id=" + myCalendarID + "&weekToLoad="+weekToSend);
}

function reloadDay(myCalendarID) {
	if (typeof myCalendarID == 'undefined') {
		myCalendarID = calendarID;
	}
	var dayToSend = currentDate.format("%Y-%m-%d");
	req.send("module=calendar&component=get_day&id=" + myCalendarID + "&dayToLoad="+dayToSend);
}

function moveBack(myCalendarID) {
  if (typeof myCalendarID == 'undefined') {
    myCalendarID = calendarID;
  }
  currentCommand = "moveNextBack";
	if (currentViewMode == "month") {
		currentDate     = currentDate.decrement('month');
		var monthToSend = currentDate.format("%Y-%m");

		if ($('numo_calendar_large') && $('numo_calendar_large').getAttribute && $('numo_calendar_large').getAttribute('rel') == myCalendarID) {
		  //alert("b");
		  req.send("module=calendar&component=get_month&id=" + myCalendarID + "&monthToLoad="+monthToSend);
		}
//alert($('numo_calendar_small'));
//alert($('numo_calendar_small').getAttribute);
//alert($('numo_calendar_small').getAttribute('rel') );
		if ($('numo_calendar_small') && $('numo_calendar_small').getAttribute && $('numo_calendar_small').getAttribute('rel') == myCalendarID) {


		  reqSmall.send("module=calendar&component=get_month&size=small&id=" + myCalendarID + "&monthToLoad="+monthToSend+"&non_month_today_color=");
		}

	} else if (currentViewMode == "day") {
		currentDate     = currentDate.decrement('day');
		var dayToSend   = currentDate.format("%Y-%m-%d");
		var monthToSend = currentDate.format("%Y-%m");

		if ($('numo_calendar_large') && $('numo_calendar_large').getAttribute && $('numo_calendar_large').getAttribute('rel') == myCalendarID) {
		  req.send("module=calendar&component=get_day&id=" + myCalendarID + "&dayToLoad="+dayToSend);
		}

		if ($('numo_calendar_small') && $('numo_calendar_small').getAttribute && $('numo_calendar_small').getAttribute('rel') == myCalendarID) {
		  reqSmall.send("module=calendar&component=get_month&size=small&id=" + myCalendarID + "&monthToLoad="+monthToSend+"&non_month_today_color=");
		}

	} else if (currentViewMode == "week") {
		currentDate = currentDate.decrement('day', 7);
		var weekToSend = currentDate.format("%Y-%m-%d");
		var monthToSend = currentDate.format("%Y-%m");

		if ($('numo_calendar_large') && $('numo_calendar_large').getAttribute && $('numo_calendar_large').getAttribute('rel') == myCalendarID) {
		  req.send("module=calendar&component=get_week&id=" + myCalendarID + "&weekToLoad="+weekToSend);
		}

		if ($('numo_calendar_small') && $('numo_calendar_small').getAttribute && $('numo_calendar_small').getAttribute('rel') == myCalendarID) {
		  reqSmall.send("module=calendar&component=get_month&size=small&id=" + myCalendarID + "&monthToLoad="+monthToSend+"&non_month_today_color=");
		}
	}
}

