function selectPeriodButton(buttonID) {
	for (var i = 0; i < $('period-selector').childNodes.length; i++) {
	  $('period-selector').childNodes[i].className = "";
	  $('period-selector').childNodes[i].firstChild.className = "";
	}

	$(buttonID).className = "selected";
	$(buttonID).firstChild.className = "selected";

}



function showEventBox(myDate) {
    //alert(myDate);
	// setup the display of the date
	var myDateInfo = myDate.split("-");
	var myYear = myDateInfo[0];
	//alert(myYear);
	var myEventBoxDateStart = document.getElementById("my-event-start");
	var myEventBoxDateEnd   = document.getElementById("my-event-end");
	var currentDateTime     = new Date();
    currentDateTime.set("year", myYear);
	var myEndTimeX          = currentDateTime.clone().increment('minute', 30);


		var myStartTime         = currentDateTime.get('hr')+":" + currentDateTime.get('min');
		var myEndTime           = myEndTimeX.get('hr')+":" + myEndTimeX.get('min');
		myEventBoxDateStart.innerHTML = Date.parse(myDate + " " + myStartTime).format('%a %b %d %I:%M %p');
		myEventBoxDateEnd.innerHTML   = new Date().parse(myDate + " " + myEndTime).format('%I:%M %p');	


    //alert(myEventBoxDateStart.innerHTML);
	//var myEventBoxDateEnd         = document.getElementById("my-event-end");
	myEventBoxDateEnd.setAttribute("value", myDate);

	// reset the date display
	closeEventTime();


	// make sure the box is moved to the center of the page
  //	var myMover  = new Fx.Move($('event-box'), {relativeTo: $('numo_calendar_large'), position: 'center'});
  //	myMover.start().chain(function() {

	var myReveal = new Fx.Reveal($('event-box'), {duration: 500, mode: 'vertical'});
	myReveal.reveal().chain(function() {
	myOverText_event_type_2__field_3 = new OverText($('event_type_2__field_3'), {element: 'span'});
	myOverText_event_type_2__field_3.show();
	myOverText_event_type_2__field_5 = new OverText($('event_type_2__field_5'), {element: 'span'});
	myOverText_event_type_2__field_5.show();
		});
	//});

	var myFirstDatePicker                  = eval('field_1_date_Object');
	myFirstDatePicker.displayed.monthIndex = new Date().parse(myDate).format('%m') - 1;
	myFirstDatePicker.displayed.yearIndex  = new Date().parse(myDate).format('%Y');
	myFirstDatePicker.displayed.yearValue  = myFirstDatePicker.displayed.yearIndex ;
	myFirstDatePicker.getYearField().value = myFirstDatePicker.displayed.yearIndex ;
	myFirstDatePicker.pickDay(new Date().parse(myDate).format('%d'));
	myFirstDatePicker.show(); // toggles the show, to actually hide it

	var mySecondDatePicker                  = eval('field_2_date_Object');
	mySecondDatePicker.displayed.monthIndex = new Date().parse(myDate).format('%m') - 1;
	mySecondDatePicker.displayed.yearIndex  = new Date().parse(myDate).format('%Y');
	mySecondDatePicker.displayed.yearValue  = mySecondDatePicker.displayed.yearIndex ;
	mySecondDatePicker.getYearField().value = mySecondDatePicker.displayed.yearIndex ;
	mySecondDatePicker.pickDay(new Date().parse(myDate).format('%d'));
	mySecondDatePicker.show(); // toggles the show, to actually hide it

  try {
		var myRepeatUntilDateInput                  = eval("repeat_until_Object");
		myRepeatUntilDateInput.displayed.monthIndex = new Date().parse(myDate).format('%m') - 1;
		myRepeatUntilDateInput.displayed.yearIndex  = new Date().parse(myDate).format('%Y');
		myRepeatUntilDateInput.displayed.yearValue  = myRepeatUntilDateInput.displayed.yearIndex ;
	    myRepeatUntilDateInput.getYearField().value = myRepeatUntilDateInput.displayed.yearIndex ;
		myRepeatUntilDateInput.pickDay(new Date().parse(myDate).format('%d'));
		myRepeatUntilDateInput.show(); // toggles the show, to actually hide it
	} catch (error) {

	}
		var myFirstTimePicker = document.getElementById("field_1_time");
	myFirstTimePicker.setAttribute("value", new Date().parse(myDate + " " + myStartTime).format('%I:%M %p'));

	var mySecondTimePicker = document.getElementById("field_2_time");
	mySecondTimePicker.setAttribute("value", new Date().parse(myDate + " " + myEndTime).format('%I:%M %p'));

}

function setDayAllDay(myCheckbox) {
	var myCheckboxIsChecked = myCheckbox.checked;

	var myFirstTimePicker  = document.getElementById("field_1_time");
	var mySecondTimePicker = document.getElementById("field_2_time");

	if (myCheckboxIsChecked) {
		myFirstTimePicker.style.display  = "none";
		mySecondTimePicker.style.display = "none";
	} else {
		myFirstTimePicker.style.display  = "block";
		mySecondTimePicker.style.display = "block";
	}
}

function setDayAllDayEdit(myCheckbox) {
	var myCheckboxIsChecked = myCheckbox.checked;

	var myFirstTimePicker  = document.getElementById("edit_field_1_time");
	var mySecondTimePicker = document.getElementById("edit_field_2_time");

	if (myCheckboxIsChecked) {
		myFirstTimePicker.style.display  = "none";
		mySecondTimePicker.style.display = "none";
	} else {
		myFirstTimePicker.style.display  = "block";
		mySecondTimePicker.style.display = "block";

	}

}
function showEventBoxDay(myDate, myStartHour, myStartMinute) {
	inBox = true;

	// setup the display of the date
	var myEventBoxDateStart = document.getElementById("my-event-start");
	var myEventBoxDateEnd   = document.getElementById("my-event-end");

	var currentDateTime = new Date();
	currentDateTime.set("hr", myStartHour);
	currentDateTime.set("min", myStartMinute);

	var myStartTime = currentDateTime.get('hr')+":" + currentDateTime.get('min');
	var myEndTimeX  = currentDateTime.clone().increment('minute', 30);
	var myEndTime   = myEndTimeX.get('hr')+":" + myEndTimeX.get('min');

	myEventBoxDateStart.innerHTML = Date.parse(myDate + " " + myStartTime).format('%a %b %d %I:%M %p');
	myEventBoxDateEnd.innerHTML   = new Date().parse(myDate + " " + myEndTime).format('%I:%M %p');

	var myEventBoxDateEnd = document.getElementById("my-event-end");
	myEventBoxDateEnd.setAttribute("value", myDate);

	// reset the date display
	closeEventTime();

	// make sure the box is moved to the center of the page
	var myMover  = new Fx.Move($('event-box'), {relativeTo: $('numo_calendar_large'), position: 'center'});
	myMover.start().chain(function() {
		var myReveal = new Fx.Reveal($('event-box'), {duration: 0, mode: 'vertical'});
		myReveal.reveal().chain(function() {
			var myOverText_field_3 = new OverText($('field_3'), {element: 'span'});
			myOverText_field_3.show();
			var myOverText_field_5 = new OverText($('field_5'), {element: 'span'});
			myOverText_field_5.show();
		});
	});

	var myFirstDatePicker                 = eval('field_1_date_Object');
	myFirstDatePicker.displayed.monthIndex = new Date().parse(myDate).format('%m') - 1;
	myFirstDatePicker.displayed.yearIndex  = new Date().parse(myDate).format('%Y');
	myFirstDatePicker.pickDay(new Date().parse(myDate).format('%d'));
	myFirstDatePicker.show(); // toggles the show, to actually hide it

	var mySecondDatePicker                  = eval('field_2_date_Object');
	mySecondDatePicker.displayed.monthIndex = new Date().parse(myDate).format('%m') - 1;
	mySecondDatePicker.displayed.yearIndex  = new Date().parse(myDate).format('%Y');
	mySecondDatePicker.pickDay(new Date().parse(myDate).format('%d'));
	mySecondDatePicker.show(); // toggles the show, to actually hide it

	var myFirstTimePicker = document.getElementById("field_1_time");
	myFirstTimePicker.setAttribute("value", new Date().parse(myDate + " " + myStartTime).format('%I:%M %p'));

	var mySecondTimePicker = document.getElementById("field_2_time");
	mySecondTimePicker.setAttribute("value", new Date().parse(myDate + " " + myEndTime).format('%I:%M %p'));
}

