var typeOfRepeats = new Array();
typeOfRepeats[0] = "daily";
typeOfRepeats[1] = "weekdays";
typeOfRepeats[2] = "mwf";
typeOfRepeats[3] = "tth";
typeOfRepeats[4] = "weekly";
typeOfRepeats[5] = "monthly";
typeOfRepeats[6] = "yearly";

function changeRepeatType(selectBox) {
	var selectedIndex = selectBox.selectedIndex;
	var selectedValue = selectBox.options[selectedIndex].value;
	var selectedText  = selectBox.options[selectedIndex].text;
	for (var i = 0; i < typeOfRepeats.length; i++) {
	  var myExtraConfigArea = document.getElementById("repeat-" + typeOfRepeats[i]);
		if (myExtraConfigArea) {
			myExtraConfigArea.style.display = "none";
		}
	}

	if (selectedValue != "") {
		var myExtraConfigArea = document.getElementById("repeat-" + selectedValue);
		if (myExtraConfigArea) {
			myExtraConfigArea.style.display = "block";
		}
	}

	var myExtraConfigArea = document.getElementById("repeat-ends");

	if (selectedValue != '') {
		myExtraConfigArea.style.display = "block";
	} else {
		myExtraConfigArea.style.display = "none";
	}
}


function changeEditRepeatType(selectBox) {
	var selectedIndex = selectBox.selectedIndex;
	var selectedValue = selectBox.options[selectedIndex].value;
	var selectedText  = selectBox.options[selectedIndex].text;

	for (var i = 0; i < typeOfRepeats.length; i++) {
	  var myExtraConfigArea = document.getElementById("edit_repeat-" + typeOfRepeats[i]);
		if (myExtraConfigArea) {
			myExtraConfigArea.style.display = "none";
		}
	}

	if (selectedValue != "") {
		var myExtraConfigArea = document.getElementById("edit_repeat-" + selectedValue);
		if (myExtraConfigArea) {
			myExtraConfigArea.style.display = "block";
		}
	}

	var myExtraConfigArea = document.getElementById("edit_repeat-ends");
	if (selectedValue != '') {
		myExtraConfigArea.style.display = "block";
	} else {
		myExtraConfigArea.style.display = "none";
	}
}

function loginBox() {
	if ($('login-link').innerHTML == "Cancel") {
    // make sure the box is moved to the center of the page
		var myReveal = new Fx.Reveal($('login-box'), {duration: 500, mode: 'vertical'});
		myReveal.dissolve();
		$('login-link').innerHTML = "Login";
	} else {
    // make sure the box is moved to the center of the page
		var myMover  = new Fx.Move($('login-box'), {relativeTo: $('login-status'), position: 'bottomRight', edge: 'topRight'});
		myMover.start().chain(function() {
		  var myReveal = new Fx.Reveal($('login-box'), {duration: 500, mode: 'vertical'});
		  myReveal.reveal();
		});
		$('login-link').innerHTML = "Cancel";
	}
}

