function closeEventBox() {
	inBox = false;

	var myReveal = new Fx.Reveal($('event-box'), {duration: 500, mode: 'vertical'});
	myReveal.dissolve();
}

function closeEditEventBox() {
	inBox = false;

	var myReveal = new Fx.Reveal($('edit-event-box'), {duration: 500, mode: 'vertical'});

	myReveal.dissolve().chain(function() {
		$("edit-loading-container").style.display       = "none";
		$("edit-repeat-change-container").style.display = "none";
		$("edit-repeat-delete-container").style.display = "none";
		$("edit-event-container").style.display         = "block";
	});

}

function closeEventTime() {
	var myDissolve = new Fx.Reveal($('event-box-when-detail'), {duration: 500, mode: 'vertical'});

	myDissolve.dissolve().chain(function() {
		var myReveal = new Fx.Reveal($('event-box-when'), {duration: 500, mode: 'vertical'});
		myReveal.reveal();
	});
}

