function showTimeSelector(timeField) {
	var myTimeSelectorFieldName = timeField.id + "-selector";

	var myReveal = new Fx.Reveal($(myTimeSelectorFieldName), {duration: 500, mode: 'vertical'});
	myReveal.toggle();
}

function hideTimeSelector(timeField) {
	var myTimeSelectorFieldName = timeField.id + "-selector";

	var myReveal = new Fx.Reveal($(myTimeSelectorFieldName), {duration: 500, mode: 'vertical'});
	myReveal.dissolve();
}

function setTimeSelection(timeSelectorField) {
	var myTimeSelectorFieldName = timeSelectorField.id;
	var myTimeFieldID           = myTimeSelectorFieldName.replace("-selector", "");
	var myTimeField             = document.getElementById(myTimeFieldID);
	var myDateFieldID           = myTimeFieldID.replace("time", "date");
	var myDateField             = timeSelectorField.form[myDateFieldID];
	var newTime                 = timeSelectorField.options[timeSelectorField.selectedIndex].text;

	// if we are updating the first field, then we need to space the second time as well
	if (myDateFieldID == "field_1_date") {
		var mySecondField = document.getElementById(myTimeFieldID.replace("1", "2"));
		var myFirstTime   = new Date();
		myFirstTime.set("hr", new Date().parse(myDateField.value + " " + newTime).format("H"));
		myFirstTime.set("min", new Date().parse(myDateField.value + " " + newTime).format("i"));

		var mySecondTime = new Date();
		mySecondTime.set("hr", new Date().parse(myDateField.value + " " + newTime).format("H"));
		mySecondTime.set("min", new Date().parse(myDateField.value + " " + newTime).format("i"));

		var difference = myFirstTime.diff(mySecondTime);
	} else {

	}

	myTimeField.setAttribute("value", newTime);
	var myReveal = new Fx.Reveal($(myTimeSelectorFieldName), {duration: 500, mode: 'vertical'});
	myReveal.dissolve();

}


function updateVisibleEventFields(selectBox) {
	var myID = selectBox.options[selectBox.selectedIndex].value;
	var myEventBoxDescription = $('event-box-description');

	for (i = 0; i < myEventBoxDescription.childNodes.length; i++) {
		var mySpan = myEventBoxDescription.childNodes[i];
		mySpan.style.display = "none";
	}
	$('event-box-description-' + myID).style.display = "inline";
}
