      '
' +
        '
{{{_highlightResult.title.value}}}'+
        '
{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
      ''+
    '';

    var scoreTemplate = '
' +
      '
' +
        '
{{{_highlightResult.away_team.value}}}{{{away_score}}}    {{{home_score}}}{{{_highlightResult.home_team.value}}}'+
        '
{{{_highlightResult.when.value}}}'+
        '
{{{_highlightResult.where.value}}}'+
        '
{{{_highlightResult.description.value}}}'+
      ''+
    '';

    var staffTemplate = '
' +
      '
' +
        ''+
      '' +
      '
' +
        '
{{{_highlightResult.full_name.value}}}'+
        '
