        templates: {
          empty: '',
          item: staffTemplate
        },
        hitsPerPage: 10
      })
    );

    search_page.start();
    search_live_feed.start();
    search_articles.start();
    search_events.start();
    search_scores.start();
    search_documents.start();
    search_staff.start();
  });

$(".group842514").colorbox({rel:'group842514', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group842514").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery842514 = $("a[rel=group842514]").colorbox({maxWidth: "80%"});
$("a#openGallery842514").click(function(e){
    e.preventDefault();
    $gallery842514.eq(0).click();
});

