    var BstrapModal = function (title, body, buttons) {
        var title = title || "";
        var body = body || "";
        var GetModalStructure = function () {
            var that = this;
            that.Id = BstrapModal.Id = "myModalLabel";  //Math.random();
            return "
" +
                    "
" +
                        "
" +
                            "
" +
                                    "×  " +
                                    "" + //body +
