//set parallax alt tag for ada compliance
function setParallaxSliderAlt()
{
    if($('.parallax-slider').length > 0)
    {
        console.log("parallax-slider should now get an alt tag");
        $('.parallax-slider').attr("alt", "parralax background decoration");
    }
    else { setTimeout(function() { setParallaxSliderAlt(); }, 100); }
}

$(document).ready(function() { setParallaxSliderAlt(); });
